UPDATE locales_npc_text SET entry = 11231, Text0_0_loc2 = null, Text0_1_loc2 = 'Moi, une cuisinière ? Je suppose que ça n\'est pas faux. Une serveuse doit savoir dépanner en cuisine de temps à autre, et au fil des années, j\'ai appris quelques recettes. Rien de formidable, mais quand même de quoi remplir le ventre substantiel de Grok.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11231;
UPDATE locales_npc_text SET entry = 11233, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis désolée, $N, mais vous en savez déjà autant que moi en matière de cuisine. Bien sûr, on ne sait jamais à l\'avance quand nos cuisiniers inventeront quelque chose de neuf, alors ne soyez pas trop timide pour revenir à l\'occasion.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11233;
