UPDATE locales_npc_text SET entry = 11302, Text0_0_loc2 = 'Allons allons, $N. Détendez-vous. Respirez profondément. Vous devez apprendre à me faire confiance.$B$BVotre manque d’expérience vous pousse à paniquer, et la panique conduit à l’exagération. Vous voyez ? En réalité, il n’y a pas de crise.$B$BMaintenant que cette question est résolue, je dois vous prier d’être moins $gnégatif:négative; à l’avenir. C’est mauvais pour le moral de l’équipe…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11302;
UPDATE locales_npc_text SET entry = 11309, Text0_0_loc2 = 'Si vous voulez parler à l’apprenti Garion, vous le trouverez dans la maison, avec Tabetha. Au moins, là, il ne peut pas voler tous les composants.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11309;
