UPDATE locales_npc_text SET entry = 11320, Text0_0_loc2 = 'Bonjour, vous ! Ces bons à rien de pirates de la baie ont organisé un blocus ! Pensez-vous pouvoir nous aider en leur lâchant quelques bombes dessus ?$B$BNotre zeppelin volé est là, sautez à bord avant qu\'il ne s\'envole !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11320;
