UPDATE locales_npc_text SET entry = 11358, Text0_0_loc2 = 'Il est parti ? Hourra !', Text0_1_loc2 = 'Il est parti ? Hourra !', Text1_0_loc2 = 'Le Cavalier sans tête était effrayant, mais je n\'ai pas eu peur ! Pas moi !', Text1_1_loc2 = 'Le Cavalier sans tête était effrayant, mais je n\'ai pas eu peur ! Pas moi !', Text2_0_loc2 = 'Le Cavalier sans tête a été chassé !', Text2_1_loc2 = 'Le Cavalier sans tête a été chassé !', Text3_0_loc2 = 'S\'il revient, vous allez de nouveau le combattre ?', Text3_1_loc2 = 'S\'il revient, vous allez de nouveau le combattre ?', Text4_0_loc2 = 'Je voudrais avoir $gun père:une mère; comme vous !', Text4_1_loc2 = 'Je voudrais avoir $gun père:une mère; comme vous !', Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11358;
