UPDATE locales_npc_text SET entry = 11362, Text0_0_loc2 = 'Ces bois brûlent depuis des temps immémoriaux. Le feu est alimenté par une magie antique, et les bois sont le territoire de reproduction de la mère des couvées et de ses concubins proto-drakes.$B$BMais, à présent, un mal oublié depuis bien longtemps s\'est réveillé, et vise à réduire à nouveau la progéniture de la mère en esclavage pour servir ses propres desseins.$B$BLes vrykuls ont créé une pertubation qui étend le domaine de la couvée et menace votre peuple.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11362;
UPDATE locales_npc_text SET entry = 11365, Text0_0_loc2 = 'Les représentants du cartel Gentepression de Bourbe-à-brac se servent de ce panneau pour afficher avis de recherche et offres d\'emploi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11365;
UPDATE locales_npc_text SET entry = 11370, Text0_0_loc2 = 'Bien sûr ! Je leur ai donné tous les avantages possibles. Je ne vois vraiment pas ce qui a pu mal tourner.$B$BTenez, $C, prenez-en un. J’ai fait une ou deux modifications… des améliorations, en fait. Si vous échouez, vous n’aurez aucune excuse !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11370;
