UPDATE locales_npc_text SET entry = 11376, Text0_0_loc2 = 'Vous devriez reculer. C\'est salissant, de vider des poissons.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11376;
UPDATE locales_npc_text SET entry = 11384, Text0_0_loc2 = 'La Société royale des apothicaires vous souhaite la bienvenue en Norfendre, $c. Bon, qu’êtes-vous $gprêt:prête; à faire pour nous aider à servir Sylvanas ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11384;
UPDATE locales_npc_text SET entry = 11403, Text0_0_loc2 = null, Text0_1_loc2 = 'La situation est pire que je ne le redoutais ! Le zeppelin n’est plus qu’un tas de métal tordu et de carburant enflammé !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11403;
UPDATE locales_npc_text SET entry = 11406, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à Theramore, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11406;
UPDATE locales_npc_text SET entry = 11418, Text0_0_loc2 = 'Toute cette canonnade sur Mörg et sur d\'autres lieux là-bas à l\'horizon, et vous croyez que ça me rapporte quoi ?$B$BCorvée de récupération de boulets !$B$BCroyez-moi, c\'est pas ce que j\'avais en tête quand je me suis engagé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11418;
UPDATE locales_npc_text SET entry = 11419, Text0_0_loc2 = null, Text0_1_loc2 = 'Avez-vous le temps de prendre un peu de repos, trésor, ou voulez-vous juste manger quelque chose et vous détendre un peu ?$B$BSi quelqu\'un l\'a mérité, c\'est bien vous !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11419;
UPDATE locales_npc_text SET entry = 11424, Text0_0_loc2 = 'À moins que ce ne soit vraiment urgent, $gcitoyen:citoyenne;, je n’ai pas le temps de parler. Les portes de Theramore sont sous ma responsabilité.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11424;
UPDATE locales_npc_text SET entry = 11426, Text0_0_loc2 = 'Le lieutenant Aden se porte toujours volontaire pour garder la porte. Ça me va.$B$BJ\'ai un poste qui me permet à la fois d\'avoir mes gardes à l\'œil ET de voir passer les filles des marchands.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11426;
UPDATE locales_npc_text SET entry = 11432, Text0_0_loc2 = 'Louée soit la Lumière! Les Défias n’y connaissent absolument rien en plongée !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11432;
UPDATE locales_npc_text SET entry = 11436, Text0_0_loc2 = 'Le capitaine Adams m’a envoyé enquêter sur ce qui se passe à Halegrince. Mais je n\'ai pas pu m\'approcher davantage. La tour vrykule abandonnée m’assure un minimum de protection contre les regards indiscrets.$B$BLes Réprouvés préparent un sale coup, là-bas. Je le sens !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11436;
