UPDATE locales_npc_text SET entry = 11469, Text0_0_loc2 = 'Heureux de vous rencontrer, $c.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11469;
UPDATE locales_npc_text SET entry = 11472, Text0_0_loc2 = null, Text0_1_loc2 = 'Il y a huit séries de cartes de Sombrelune : Bénédictions, Déraison, Élémentaires, Fauves, Furies, Orages, Portails et Seigneurs de guerre. Chacune d\'entre elle comporte huit cartes, de l\'as au huit. Si vous les réunissez toutes les huit, vous pourrez former une suite à remettre au professeur Thaddeus Paleo en échange d\'un puissant bijou.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11472;
UPDATE locales_npc_text SET entry = 11473, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'esprit du Cavalier sans tête est rongé par la démence ! Durant la Sanssaint, il peut attaquer le village à tout instant.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11473;
UPDATE locales_npc_text SET entry = 11474, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Cavalier sans tête a mis le feu au village ! Il faut protéger les enfants !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11474;
UPDATE locales_npc_text SET entry = 11475, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Cavalier sans tête attaque ! Vite, aidez vos frères de la Horde à défendre le village !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11475;
UPDATE locales_npc_text SET entry = 11476, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons repoussé le Cavalier sans tête pour l\'instant. Nous pouvons respirer un peu, mais il faut rester sur nos gardes !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11476;
UPDATE locales_npc_text SET entry = 11477, Text0_0_loc2 = 'La Sanssaint est une fête qui fait peur, mais je l\'aime bien !', Text0_1_loc2 = 'La Sanssaint est une fête qui fait peur, mais je l\'aime bien !', Text1_0_loc2 = 'Vous avez vu le Cavalier sans tête ? S\'il vient ici, je vais me battre contre lui et lui voler sa citrouille !', Text1_1_loc2 = 'Vous avez vu le Cavalier sans tête ? S\'il vient ici, je vais me battre contre lui et lui voler sa citrouille !', Text2_0_loc2 = 'J\'aime jouer dans le baril, mais on m\'a dit que c\'est pas pour les enfants.', Text2_1_loc2 = 'J\'aime jouer dans le baril, mais on m\'a dit que c\'est pas pour les enfants.', Text3_0_loc2 = 'Les grands disent que le baril et les seaux, c\'est pour éteindre le feu. Vous êtes un pompier ?', Text3_1_loc2 = 'Les grands disent que le baril et les seaux, c\'est pour éteindre le feu. Vous êtes un pompier ?', Text4_0_loc2 = 'Le Cavalier sans tête vit au Monastère écarlate avec tout plein d\'autres fantômes et d\'humains effrayants.', Text4_1_loc2 = 'Le Cavalier sans tête vit au Monastère écarlate avec tout plein d\'autres fantômes et d\'humains effrayants.', Text5_0_loc2 = 'Ils distribuent des bonbons et des friandises à l\'auberge ! La gardienne Guet-sinistre dit que si on ne nous donne pas de bonbon… alors on a le droit de faire une blague !', Text5_1_loc2 = 'Ils distribuent des bonbons et des friandises à l\'auberge ! La gardienne Guet-sinistre dit que si on ne nous donne pas de bonbon… alors on a le droit de faire une blague !', Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11477;
