UPDATE locales_npc_text SET entry = 11492, Text0_0_loc2 = 'Bienvenue à Theramore, $c.', Text0_1_loc2 = 'Bienvenue à Theramore, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11492;
UPDATE locales_npc_text SET entry = 11494, Text0_0_loc2 = 'Nous n\'aurions pas dû laisser tant de gens à Valgarde. Cet amiral Keller est d\'une vanité sans nom !$B$BComment puis-je accomplir ma mission avec si peu d\'hommes ? Il m\'en faut assez pour rester et tenir le donjon, tout en mettant une garnison suffisante au fort Hardivar dès qu\'il sera prêt !$B$BAu moins nous avons une occasion de nous occuper de nos ennuis maintenant que vous êtes là.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11494;
UPDATE locales_npc_text SET entry = 11496, Text0_0_loc2 = 'J\'apporte mon conseil au capitaine sur les sujets dont il choisit de me faire part, mais j\'ai l\'impression qu\'il préfère s\'adresser à vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11496;
UPDATE locales_npc_text SET entry = 11498, Text0_0_loc2 = null, Text0_1_loc2 = 'Toutes ces bouches à nourrir, et qui doit s\'en occuper ? Moi !$B$BToute la cuisine, tout le ménage, et tout le reste !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11498;
UPDATE locales_npc_text SET entry = 11502, Text0_0_loc2 = 'De toute ma vie, je n\'aurais jamais pensé voir s\'ouvrir une nouvelle frontière aussi magnifique.$B$BSi seulement elle n\'était pas souillée par le mal et par un savoir qui devrait rester caché.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11502;
UPDATE locales_npc_text SET entry = 11504, Text0_0_loc2 = 'J\'ai vu un bon paquet de choses dans ma carrière, mais le changement qui a touché la Ligue des explorateurs au goulet des Murmures remporte la palme.$B$BJe suis juste bien content d\'avoir pu me sortir de là sans y laisser ma peau.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11504;
