UPDATE locales_npc_text SET entry = 11562, Text0_0_loc2 = 'Le sol… est gelé. Nous faisons une cible idéale, ici. Les chariots à viande n\'iront nulle part.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11562;
UPDATE locales_npc_text SET entry = 11564, Text0_0_loc2 = 'D\'après vous, quel est le problème ? Le sol est gelé, aussi dur que de la pierre !$B$BLes roues des chariots à viande ont été conçues pour rouler sur la boue, au mieux. Il va nous falloir une abomination et des chaînes pour réussir à les sortir de là.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11564;
UPDATE locales_npc_text SET entry = 11567, Text0_0_loc2 = 'Je ne me sentirai jamais chez moi à Mur-de-Fougères. Ce serait abandonner tout espoir de reprendre les terres qui nous appartiennent de droit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11567;
