UPDATE locales_npc_text SET entry = 11607, Text0_0_loc2 = 'Entrez vous mettre à l\'abri du froid et reposer un peu votre corps fatigué. Nous vous offrons l\'hospitalité.', Text0_1_loc2 = 'Entrez vous mettre à l\'abri du froid et reposer un peu votre corps fatigué. Nous vous offrons l\'hospitalité.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11607;
UPDATE locales_npc_text SET entry = 11614, Text0_0_loc2 = 'Allez dans la Lumière, mon enfant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11614;
UPDATE locales_npc_text SET entry = 11622, Text0_0_loc2 = '$gMonsieur:Madame; ! Je suis ici pour vous aider et vous protéger.$B$BQuels sont mes ordres ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11622;
UPDATE locales_npc_text SET entry = 11625, Text0_0_loc2 = 'Des années durant, j\'ai erré, perdu. Maintenant, enfin, je vois dans quelle noirceur mon âme avait sombré…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11625;
UPDATE locales_npc_text SET entry = 11645, Text0_0_loc2 = 'La tête de citrouille rougeoie d\'un air funeste…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11645;
UPDATE locales_npc_text SET entry = 11655, Text0_0_loc2 = null, Text0_1_loc2 = 'Hâtez-vous, $c !$B$BLe problème est loin d\'être sous contrôle.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11655;
UPDATE locales_npc_text SET entry = 11662, Text0_0_loc2 = 'Vous voulez vous rendre quelque part, $N ? Mes chauves-souris vous y conduiront en un rien de temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11662;
UPDATE locales_npc_text SET entry = 11667, Text0_0_loc2 = 'Attention où vous mettez les pieds, $r.$B$BCes corps sont tout ce qui reste de mes meilleurs hommes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11667;
