UPDATE locales_npc_text SET entry = 11742, Text0_0_loc2 = 'Gorth a pas peur d\'être tué. Les Réprouvés, ils vont juste me recoudre.', Text0_1_loc2 = null, Text1_0_loc2 = 'Gorth comprend pas tout, des fois. Gorth tue bien les gens, alors Gorth devient sergent. Mais maintenant, Gorth a plus personne à tuer !', Text1_1_loc2 = null, Text2_0_loc2 = 'Les vrykuls, ils sont plus grands et plus forts. Mais nous, on est plus beau !', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11742;
