UPDATE locales_npc_text SET entry = 11966, Text0_0_loc2 = 'À la Sanssaint, nous fêtons le jour où la reine banshee elle-même nous délivra des griffes du roi-liche et du Fléau. Ne manquez pas la Fête de l\'homme d\'osier, tenue chaque soir aux abords de Fossoyeuse.', Text0_1_loc2 = 'À la Sanssaint, nous fêtons le jour où la reine banshee elle-même nous délivra des griffes du roi-liche et du Fléau. Ne manquez pas la Fête de l\'homme d\'osier, tenue chaque soir aux abords de Fossoyeuse.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11966;
