UPDATE locales_npc_text SET entry = 12097, Text0_0_loc2 = 'Nous avons construit cet endroit de nos mains ! Sang et larmes, mes amis.$B$BVous pouvez me croire : nous n\'envisageons pas d\'abandonner Valgarde un jour.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12097;
UPDATE locales_npc_text SET entry = 12098, Text0_0_loc2 = 'Hé, mon amie, ce que j\'ai là représente une révolution technologique comme on n\'en a encore jamais vu ! Et c\'est à vendre, et pas cher ! $B$BVoyez-vous, j\'en ai eu assez de dépenser mon argent dans les tavernes à la recherche de l\'âme sœur. J\'ai donc fabriqué ces lunettes et, maintenant, TOUT LE MONDE est d\'une beauté la plus parfaite qui soit. Je veux dire, avec ces bijoux, vous ne pouvez tout simplement PAS mal tomber !$B$BAlors, vous voulez les essayer, et m\'aider un peu à mettre du beurre dans mes chénopodiacées ? Et comme vous êtes super mignonne, je vais même vous faire une réduction !', Text0_1_loc2 = 'Hé, mon ami, ce que j\'ai là représente une révolution technologique comme on n\'en a encore jamais vu ! Et c\'est à vendre, et pas cher ! $B$BVoyez-vous, j\'en ai eu assez de dépenser mon argent dans les tavernes à la recherche de l\'âme sœur. J\'ai donc fabriqué ces lunettes et, maintenant, TOUT LE MONDE est d\'une beauté la plus parfaite qui soit. Je veux dire, avec ces bijoux, vous ne pouvez tout simplement PAS mal tomber !$B$BAlors, vous voulez les essayer, et m\'aider un peu à mettre du beurre dans mes chénopodiacées ? Et comme vous êtes super mignon, je vais même vous faire une réduction !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12098;
