UPDATE locales_npc_text SET entry = 12103, Text0_0_loc2 = 'Les chiens normaux ne m\'approchent plus, de nos jours. Ce fut un coup terrible quand je m\'en suis aperçu, car j\'adorais les chiens.$B$BLes chiens pestiférés sont un bon substitut, cela dit. C\'est vrai, ils ne sont pas aussi… agréables à l\'œil. Mais bon, moi non plus !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12103;
