UPDATE locales_npc_text SET entry = 12113, Text0_0_loc2 = 'L\'accident sur la route de Tranchecolline a porté un sacré coup à notre approvisionnement en bière ! Et le fait que les nains Sombrefer nous attaquent toutes les heures n\'arrange rien !$B$BDonc si vous voulez nous aider, voici ma proposition. Je vous laisse emprunter un de mes béliers et je vous passe les rênes.$B$BChaque fois que vous me rapporterez un tonneau, je vous donnerai quelques jetons. Je vous laisserai même garder le bélier un peu plus longtemps pour chaque tonneau rapporté !$B$BMAIS, une fois que vous aurez commencé, vous ne pourrez pas y retourner avant le lendemain.
$B$BVous êtes $gsûr:sûre; de vouloir y aller ?', Text0_1_loc2 = 'L\'accident sur la route de Tranchecolline a porté un sacré coup à notre approvisionnement en bière ! Et le fait que les nains Sombrefer nous attaquent toutes les heures n\'arrange rien !$B$BDonc si vous voulez nous aider, voici ma proposition. Je vous laisse emprunter un de mes béliers et je vous passe les rênes.$B$BChaque fois que vous me rapporterez un tonneau, je vous donnerai quelques jetons. Je vous laisserai même garder le bélier un peu plus longtemps pour chaque tonneau rapporté !$B$BMAIS, une fois que vous aurez commencé, vous ne pourrez pas y retourner avant le lendemain.
$B$BVous êtes $gsûr:sûre; de vouloir y aller ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12113;
UPDATE locales_npc_text SET entry = 12114, Text0_0_loc2 = 'Je vois que vous vous intéressez à nos béliers de course obtenus en toute légalité... Vous voulez sentir l\'ivresse de la course de bélier... Mais avant de jouer, il va falloir travailler.$B$BEncore qu\'avec ces béliers, jouer ou travailler c\'est du pareil au même.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12114;
