UPDATE locales_npc_text SET entry = 12119, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12119;
UPDATE locales_npc_text SET entry = 12120, Text0_0_loc2 = 'Ho, du bateau, mec ! Mais qu\'est-ce qui s\'passe, cw\'é vieux loup d\'mer !$B$BHé mais c\'est le box gw\'ave ici, vieux fow\'ban !', Text0_1_loc2 = null, Text1_0_loc2 = 'Ou \'w en jettes mow\'tel, moussaillon ! \'W as pillé un gw\'os galion ? Faut faire touw\'ner les lingots… Fais pas l\'boit-sans-soif, mec !', Text1_1_loc2 = null, Text2_0_loc2 = 'Mate un peu paw\' là, matelot ! Y a un busard scw\'ofuleux qui titille le bouseux. Li va tâter du sel, c\'te vieux cabot d\'gabier… Li va faiw\' un tour sous la gw\'ande cale, tout zonbi qu\'y va fini.', Text2_1_loc2 = null, Text3_0_loc2 = 'C\'est pas du manti, mec ! Y a pas pu haw\'dis matelots qu\'les Flibustiers des mers du Now\' qu\'tu vois là, mec !$B$BGaw\' à pas fé fache, mec. Y a pas à koze. Les moussaillons qui font les babas, y causent au chat à neuf queues, mec !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12120;
UPDATE locales_npc_text SET entry = 12121, Text0_0_loc2 = 'Oh, wa va là pas m\'bw\'iquer la caw\'lingue, mec ! Tu jettes à me baw\'der ou quoi, wa l\'sale bâtaw\'d à écw\'ouelles !$B$BAlow\'s calte, foie bilieux d\'cw\'apule à scw\'ofule ! À la r\'voiluw\'e !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12121;
UPDATE locales_npc_text SET entry = 12122, Text0_0_loc2 = 'Alors c\'est vous la nouvelle vedette de la ville, hein ?$B$B<Zorek crache par terre.>$B$BOuais…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12122;
UPDATE locales_npc_text SET entry = 12124, Text0_0_loc2 = '<Le vieux nageglace dessine sur le sable. Il vous semble d\'après ses dessins simplistes qu\'il demande votre aide.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12124;
UPDATE locales_npc_text SET entry = 12130, Text0_0_loc2 = '<Le vieux nageglace vous observe d\'un air méfiant. Son œil vitreux cligne quand il secoue la tête de bas en haut en guise d\'au revoir.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12130;
UPDATE locales_npc_text SET entry = 12134, Text0_0_loc2 = 'Il n\'y a aucun autre festival où on boit et festoie autant qu\'à la fête des Brasseurs !', Text0_1_loc2 = 'Il n\'y a aucun autre festival où on boit et festoie autant qu\'à la fête des Brasseurs !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12134;
UPDATE locales_npc_text SET entry = 12135, Text0_0_loc2 = 'J\'ai un conseil pour les gens avisés, $gétranger:étrangère; : retournez d\'où vous venez.$B$BCet endroit n\'est pas pour les aventuriers fragiles. Les gens comme vous, cet endroit n\'en fait qu\'une bouchée avant de recracher leurs os.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12135;
UPDATE locales_npc_text SET entry = 12137, Text0_0_loc2 = 'Cette énorme citrouille de la Sanssaint trône au milieu du village.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12137;
UPDATE locales_npc_text SET entry = 12138, Text0_0_loc2 = 'La citrouille a été éclatée, et ne renferme plus aucun trésor.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12138;
