UPDATE locales_npc_text SET entry = 12144, Text0_0_loc2 = 'Quoi-t-est-ce ? Voulez voler ? Nan… Aucu-auc-auc-aucune chan-chanchanchan-chance !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12144;
UPDATE locales_npc_text SET entry = 12152, Text0_0_loc2 = 'Bienvenue sur le site de la fête des Brasseurs, mon $gami:amie; ! Vous trouverez ici tout ce qu\'il faut pour vous amuser pendant que nous célébrons l\'esprit de compétition amicale entre les brasseries concurrentes. Si vous avez la moindre question, venez me trouver !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12152;
UPDATE locales_npc_text SET entry = 12153, Text0_0_loc2 = 'Salutations, $r. Je suis Hidalgo, maître chasseur et fauconnier de renommée mondiale. La machine à tuer que vous voyez perchée sur mon épaule est Excelsior.$B$BAlors vous vous demandez sans doute : « Mais, un fauconnier, qu\'est-ce que cela fait, au juste ? ». Ce que nous faisons, je vais vous le dire : nous chassons ! La fauconnerie est l\'art de diriger un oiseau de proie pour chasser et attraper le gibier. Peut-être voudriez-vous apprendre à chasser avec un rapace dressé ? Hidalgo peut vous enseigner cet art !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12153;
