UPDATE locales_npc_text SET entry = 12157, Text0_0_loc2 = 'Salut, et bienvenue à la fête des Brasseurs ! Il y a tout un tas de façons de vous amuser par ici, donc, si vous avez la moindre question, n\'hésitez pas à venir me voir. Sinon, allez chercher une bière et un bretzel !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12157;
UPDATE locales_npc_text SET entry = 12158, Text0_0_loc2 = 'Ce qui fait la fête des Brasseurs, c\'est la bière et la nourriture, et il n\'y a pas meilleur que ce que nous servons. Vous croyez que c\'est des boniments ? Alors jetez un œil à toutes les bonnes bières, saucisses, bretzels et fromages que nous avons. Vous verrez : il n\'y a pas mieux à se mettre sous la dent, et vous ne trouverez pas moins cher !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12158;
