UPDATE locales_npc_text SET entry = 12165, Text0_0_loc2 = 'Moi j\'vous l\'dis, y a qu\'un moyen de chasser correctement : complètement beurré !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12165;
UPDATE locales_npc_text SET entry = 12167, Text0_0_loc2 = '<La créature vous picore la main.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12167;
UPDATE locales_npc_text SET entry = 12168, Text0_0_loc2 = '<L\'épervier réclame une larve en glapissant.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12168;
UPDATE locales_npc_text SET entry = 12170, Text0_0_loc2 = 'J\'vous conseille de baisser la tête, « moussaillon » ! Si v\'voyez ce que je veux dire !$B$BYaaaarrrr !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12170;
UPDATE locales_npc_text SET entry = 12173, Text0_0_loc2 = 'C\'est moi qui organise les combats par ici. Si vous voulez faire des paris, misez en toute confiance. Ceux qui s\'amusent à régler des combats, ils finissent sur la planche.$B$BEt je m\'en occupe moi-même !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12173;
UPDATE locales_npc_text SET entry = 12174, Text0_0_loc2 = 'Un trésor elfe de sang n\'est pas un trésor comme les autres. Ce n\'est pas simplement l\'or ou la soie ou les gemmes qui le composent, c\'est aussi la facture sin\'doreï qui est à elle seule d\'une très grande valeur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12174;
UPDATE locales_npc_text SET entry = 12175, Text0_0_loc2 = 'Je paierai ! Je paierai ! Eeeek ! Pitié, ne me faites pas de mal !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12175;
UPDATE locales_npc_text SET entry = 12176, Text0_0_loc2 = null, Text0_1_loc2 = 'Toute la journée, tout ce que Jonas le Cinglé me fait faire, c\'est briquer le pont.$B$BBriquer le pont ?$B$BJe suis un mage, la brique c\'est PAS pour moi ! En tout cas, pas de la manière vulgaire.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12176;
UPDATE locales_npc_text SET entry = 12178, Text0_0_loc2 = '<Ce pirate a manifestement forcé un peu sur la bouteille. Il semble être hors de combat pour un petit moment.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12178;
UPDATE locales_npc_text SET entry = 12179, Text0_0_loc2 = null, Text0_1_loc2 = 'Et qu\'est-ce que ce sera ? Un rhum ? Une bière ?$B$BOu quelque chose d\'autre, peut-être ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12179;
UPDATE locales_npc_text SET entry = 12180, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous voulez jouer un tour à Jack sous le nez de ses propres hommes ?$B$BÇa réclame une certaine dose de… stupidité. Bon. Mais ça va vous coûter. Je ne vais pas risquer ma vie pour moins d\'une pièce d\'or. Et vous feriez mieux d\'attendre que je sois hors d\'atteinte avant de vous lancer.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12180;
UPDATE locales_npc_text SET entry = 12181, Text0_0_loc2 = 'Avoir une excuse pour boire, ça ne fait jamais de mal !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12181;
UPDATE locales_npc_text SET entry = 12182, Text0_0_loc2 = 'Il y a quelque chose qui a vraiment énervé les nains Sombrefer. Ils débarquent quasiment toutes les demi-heures, cassent tous les tonneaux et repartent !$B$BJ\'imagine que vous vous demandez : « Pourquoi j\'en aurais quelque chose à faire ? »$B$BJe vais vous le dire... Parce que perdre tous ces tonneaux nous empêche de distribuer nos bières gratuites ! Vous avez bien entendu : de la bière gratuite !$B$BAlors si vous êtes dans le coin quand les nains Sombrefer arrivent, prenez une minute pour nous aider à protéger les tonneaux !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12182;
UPDATE locales_npc_text SET entry = 12183, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12183;
UPDATE locales_npc_text SET entry = 12185, Text0_0_loc2 = 'Bon retour parmi nous, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12185;
UPDATE locales_npc_text SET entry = 12186, Text0_0_loc2 = null, Text0_1_loc2 = 'Ce satané Jonah Sterling va nous conduire droit à notre perte. S\'il passait plus de temps ailleurs que dans sa caverne, il se rendrait compte qu\'il est complètement fou d\'attaquer des flottes professionnelles en temps de guerre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12186;
UPDATE locales_npc_text SET entry = 12187, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous feriez mieux de déguerpir, $N. Je ne veux pas que la mort de Jonas puisse être reliée à moi.$B$BJ\'aurai déjà assez de mal à m\'imposer comme chef de cette bande sans qu\'ils sachent que j\'ai fait tuer leur précédent capitaine.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12187;
UPDATE locales_npc_text SET entry = 12188, Text0_0_loc2 = 'Le vent apporte beaucoup de changements à nos terres et nos mers : vous, eux… Beaucoup de changements.$B$BCes changements menacent la façon de vivre de Kamagua. Qu\'allez-vous faire ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12188;
UPDATE locales_npc_text SET entry = 12189, Text0_0_loc2 = 'Alors comme ça je ne suis pas dans mon élément, hein ? Walt ne sait pas de quoi il parle ! À moi tout seul, j\'ai dézingué les nains de fer. Une belle tôle, que je leur ai collée. Ils ont méchamment dérouillé. Ensuite, j\'ai monté le camp pour observer les allées et venues sur le site de fouilles.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12189;
UPDATE locales_npc_text SET entry = 12190, Text0_0_loc2 = 'Le Duc, que vous voyez là-bas, n\'est pas content pour son tapis.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12190;
UPDATE locales_npc_text SET entry = 12191, Text0_0_loc2 = 'Soir, $gm\'sieur:m\'zelle;.', Text0_1_loc2 = 'Soir, $gm\'sieur:m\'zelle;.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12191;
UPDATE locales_npc_text SET entry = 12193, Text0_0_loc2 = 'Chuppose qu\'vous voulez r\'tourner à vot\'avant-poste. Gné ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12193;
UPDATE locales_npc_text SET entry = 12194, Text0_0_loc2 = 'Le plan de travail de Walt est à votre disposition.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12194;
UPDATE locales_npc_text SET entry = 12195, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12195;
UPDATE locales_npc_text SET entry = 12196, Text0_0_loc2 = 'Deux fois par jour, à six heures et quart et à dix-huit heures quinze, le chasseur des ombres Vol\'jin vient en personne à la fête des Brasseurs pour la cérémonie de la mise en perce. Ce serait bête de rater ça !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12196;
UPDATE locales_npc_text SET entry = 12198, Text0_0_loc2 = 'Ce tonneau de bière est surveillé par Jarven Tonnebière. Tant qu\'il est à la cave, impossible de toucher aux tonneaux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12198;
UPDATE locales_npc_text SET entry = 12207, Text0_0_loc2 = 'Le cap\'taine Magot, c\'est le MEILLEUR !', Text0_1_loc2 = null, Text1_0_loc2 = 'J\'m\'occupe de briquer le pont !', Text1_1_loc2 = null, Text2_0_loc2 = 'La Dame Mehley est le meilleur bateau du MONDE !', Text2_1_loc2 = null, Text3_0_loc2 = 'Jouer de la brique m\'a rendu moins rachitique !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12207;
UPDATE locales_npc_text SET entry = 12208, Text0_0_loc2 = 'Les temps changent, $N. La guerre est venue.$B$BNotre peuple va-t-il saisir cette occasion et aider nos alliés… Ou allons-nous rester en marge de l\'Histoire ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12208;
UPDATE locales_npc_text SET entry = 12209, Text0_0_loc2 = 'J\'ai pas le temps de bavarder, désolé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12209;
UPDATE locales_npc_text SET entry = 12210, Text0_0_loc2 = null, Text0_1_loc2 = 'Voyez donc pas que j\'travaille, $gmon garçon:ma fille; ? Du vent, avant qu\' Kowalski ne m\'voie en train d\'bâiller aux corneilles !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12210;
UPDATE locales_npc_text SET entry = 12211, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12211;
UPDATE locales_npc_text SET entry = 12212, Text0_0_loc2 = 'Torgoley nous fait bosser dur, mais c\'est un bon capitaine, ouais.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12212;
UPDATE locales_npc_text SET entry = 12213, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12213;
UPDATE locales_npc_text SET entry = 12214, Text0_0_loc2 = 'Je dois garder l\'œil sur le bateau et son équipage, $gmon bon monsieur:ma bonne dame;. Veuillez me pardonner de ne pas m\'arrêter pour parler avec vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12214;
UPDATE locales_npc_text SET entry = 12215, Text0_0_loc2 = 'La Dame Mehley vient assez souvent à Theramore pour que nous ayons été affectés à la garde de ses passagers.', Text0_1_loc2 = 'La Dame Mehley vient assez souvent à Theramore pour que nous ayons été affectés à la garde de ses passagers.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12215;
UPDATE locales_npc_text SET entry = 12216, Text0_0_loc2 = 'Bienvenue sur la majestueuse dame Mehley. Je suis le capitaine Torgoley.$B$BSi vous avez la moindre question à propos du navire, n\'hésitez pas. Sinon, je vous prie, profitez du voyage, comme je ne manque jamais de le faire… Après tout, pour citer un de mes scribes préférés : dans mon expérience, on fait sur un bateau une découverte intéressante à propos du monde. On s\'aperçoit qu\'on peut s\'en passer complètement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12216;
UPDATE locales_npc_text SET entry = 12217, Text0_0_loc2 = 'Oui, c\'est son nom, et c\'est un bien beau bateau.$B$BLes navires ont beaucoup à nous apprendre, savez-vous. Un grand poète a jadis déclaré : Les vaisseaux qui passent dans la nuit, et se parlent en passant, montrent seul un signal, et une voix lointaine dans le noir ; Ainsi, sur l\'océan de la vie, passons-nous et nous parlons-nous, seul un regard et une voix, puis à nouveau le noir et un silence.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12217;
UPDATE locales_npc_text SET entry = 12218, Text0_0_loc2 = 'Si vous désirez vous sustenter, la cambusière Grace pourra répondre à vos besoins.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12218;
UPDATE locales_npc_text SET entry = 12219, Text0_0_loc2 = 'Monsieur Combs, l\'ingénieur et intendant du navire, pourra vous fournir tout l\'équipement dont vous pourriez avoir besoin. Il est également rompu à l\'art du bricolage, si votre matériel a besoin de réparations.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12219;
UPDATE locales_npc_text SET entry = 12220, Text0_0_loc2 = 'Ah, oui… le nom. Eh bien… comme on dit, un bon nom, tout comme la bonne volonté, se gagne par de nombreux actes et ne se perd que par un seul. Nul besoin d\'entrer dans les détails du comment ou du pourquoi ; il se trouve tout simplement que c\'est ainsi que certains me nomment.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12220;
UPDATE locales_npc_text SET entry = 12221, Text0_0_loc2 = 'Les morts se relèvent, et exercent leur vengeance sur tous ceux qui osent traverser leur domaine. Rebroussez chemin !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12221;
