UPDATE locales_npc_text SET entry = 12275, Text0_0_loc2 = 'Bienvenue à bord du bac de Pennelune. Nous parcourons les eaux qui séparent le bastion de Pennelune de la Côte oubliée à la recherche des gens perdus en mer. L\'océan peut être dangereux si vous n\'êtes pas bien $gpréparé:préparée; à l\'affronter.$B$BSi vous avez des préparatifs à faire, allez trouver nos ravitailleurs. Ils seront très heureux de vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12275;
UPDATE locales_npc_text SET entry = 12276, Text0_0_loc2 = 'Au nom de l\'équipage de l\'Écume de lune, je vous souhaite la bienvenue, $c. J\'espère que vous profiterez de nos provisions avant de nous quitter.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12276;
UPDATE locales_npc_text SET entry = 12277, Text0_0_loc2 = 'Attention où vous mettez les pieds à bord de la Bénédiction d\'Elune, $r. Même si la découverte de nos nouveaux amis est effectivement un don d\'Elune, nous avons aussi eu notre lot de tragédies en mer.$B$BMais pouvoir participer au sauvetage des âmes perdues en mer ou dans un nouveau monde est bien une bénédiction que nous offre Elune.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12277;
UPDATE locales_npc_text SET entry = 12278, Text0_0_loc2 = 'Elune nous a maintes fois offert sa bénédiction sous la forme de toutes les découvertes que nous avons faites. Mais nous devons rester vigilants, $c.', Text0_1_loc2 = 'Elune nous a maintes fois offert sa bénédiction sous la forme de toutes les découvertes que nous avons faites. Mais nous devons rester vigilants, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12278;
UPDATE locales_npc_text SET entry = 12279, Text0_0_loc2 = null, Text0_1_loc2 = 'Pardonnez-moi, $r, je dois me concentrer sur mon travail.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12279;
UPDATE locales_npc_text SET entry = 12280, Text0_0_loc2 = null, Text0_1_loc2 = 'Je m\'excuse, mais j\'ai peu de temps pour parler à quelqu\'un d\'autre qu\'aux feux follets ou au capitaine à qui je fais mon rapport. Pardonnez-moi.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12280;
UPDATE locales_npc_text SET entry = 12281, Text0_0_loc2 = 'Bienvenue à bord du Caprice de la vierge. La traversée devrait être calme.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12281;
UPDATE locales_npc_text SET entry = 12282, Text0_0_loc2 = 'C\'est impressionnant tout ce que peut vous apprendre un gnome si vous le pressez assez fort : d\'ingénieux petits tours pour échapper à ses liens, la recette de délicieux gâteaux, et même comment fabriquer une machine volante.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12282;
UPDATE locales_npc_text SET entry = 12283, Text0_0_loc2 = 'Aussi souvent que possible. C\'est un excellent exercice, et ça me permet de me tenir au courant de ce qu\'ils mijotent. Ce qui, d\'après mon expérience, n\'est jamais rien de bon.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12283;
UPDATE locales_npc_text SET entry = 12284, Text0_0_loc2 = 'Euuurrrrk… Si ce tâcheron de cambusier qui se trouve là-bas vous offre sa spécialité, rendez-vous un service à vous-même : balancez-la à la baille. Elle éliminera peut-être quelques makruras au lieu de votre envie de vivre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12284;
UPDATE locales_npc_text SET entry = 12285, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12285;
UPDATE locales_npc_text SET entry = 12286, Text0_0_loc2 = null, Text0_1_loc2 = 'Bonjour, $N.$B$BMaintenant que j\'ai fini de fabriquer mon enclume, je peux commencer à équiper les troupes. C\'est un pas important, mais il y a encore beaucoup à faire.$B$BEt, plus important, en ce qui vous concerne, je peux créer de nouveaux objets qui pourraient vous intéresser.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12286;
UPDATE locales_npc_text SET entry = 12287, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons pris le port, et ce bateau est un joli bonus. Dommage qu\'il ne soit pas encore prêt à naviguer.$B$BEn attendant, j\'ai reçu l\'ordre d\'organiser la liquidation des nagas de la côte de Verte-branchie.$B$BVous pensez pouvoir m\'aider ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12287;
UPDATE locales_npc_text SET entry = 12288, Text0_0_loc2 = 'Bienvenue à bord du Mande-tonnerre !$B$BNe faites pas attention aux gens qui se plaignent d\'explosions ou autres. Non enfin, je veux dire, l\'erreur est gobeline. Il faudrait jeter du lest, un peu, si vous voyez ce que je veux dire.$B$BD\'accord, je comprendrais, si un capitaine se débrouillait pour s\'écraser 22 ou 23 fois... Mais à moins de ça, ce n\'est vraiment pas la peine d\'en faire un fromage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12288;
UPDATE locales_npc_text SET entry = 12289, Text0_0_loc2 = '<Marmonne> J\'imagine que ces montagnes sont apparues comme par magie… Les cinq fois !', Text0_1_loc2 = null, Text1_0_loc2 = '<Marmonne> On pourrait penser qu\'il est difficile de réussir à s\'écraser dans l\'océan. Surtout quatre fois de suite !', Text1_1_loc2 = null, Text2_0_loc2 = '<Marmonne> J\'aimerais pouvoir dire qu\'on a réussi à éviter d\'en arriver aux « atterrissages d\'urgence » dans les lacs. J\'aimerais même pouvoir dire qu\'on ne s\'est écrasés dans un lac qu\'une seule fois… Mais on a testé quatre lacs différents, y compris le lac Lordamere !', Text2_1_loc2 = null, Text3_0_loc2 = 'Je suis impressionné de voir que les branches de pins ne semblent pas beaucoup abîmer le vaisseau.$B$BMais ça n\'excuse pas les trois fois où on s\'est écrasés dans une forêt.', Text3_1_loc2 = null, Text4_0_loc2 = 'Je n\'aurais jamais pensé voir Teldrassil un jour… Je n\'aurais jamais pensé m\'écraser contre Teldrassil un jour, non plus.', Text4_1_loc2 = null, Text5_0_loc2 = 'Ça fait deux fois qu\'on réussit à passer entre les Pitons du tonnerre ! Bon, ça touchait un peu…', Text5_1_loc2 = null, Text6_0_loc2 = '<Marmonne> Je n\'arrive toujours pas à comprendre comment il a pu réussir à se faufiler entre les Milles pointes sans une égratignure, tout ça pour s\'écraser au beau milieu des Salines !', Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12289;
UPDATE locales_npc_text SET entry = 12290, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous aimez le grand frisson, notre capitaine est le meilleur ! J\'adore ce boulot !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12290;
UPDATE locales_npc_text SET entry = 12291, Text0_0_loc2 = 'Ne me demandez pas…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12291;
UPDATE locales_npc_text SET entry = 12292, Text0_0_loc2 = 'Tous ces petits contretemps, ce n\'est pas de ma faute… Je ne vois même pas où je vais !$B$BQuoique, ça veut peut-être dire que c\'est un peu de ma faute…$B$BNon ! Attendez ! Je peux amener ce navire à quai au millimètre… Ça ne peut que venir des ordres que donne le capitaine ! Peut-être qu\'il aime nous voir repousser nos limites, et tester notre capacité de survie et nos dons de réparateurs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12292;
UPDATE locales_npc_text SET entry = 12294, Text0_0_loc2 = 'Avant, je m\'appelais Iolol, jusqu\'à ce qu\'un vote à bulletin secret soit organisé en ville pour que je change de nom. C\'était ça, ou suivre le chemin de Corki. Vous n\'auriez pas hésité non plus. Que pensez-vous de mon nouveau nom ? Il y a un sacré progrès, n\'est-ce pas ?$B$BEt tant que j\'y suis, jetez un œil au panneau d\'affichage si vous cherchez du travail.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12294;
