UPDATE locales_npc_text SET entry = 12309, Text0_0_loc2 = 'Si Kil\'Jaeden émerge du Puits du soleil, notre monde sera plongé dans une guerre dont il n\'a pas vu la pareille depuis dix mille ans !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12309;
UPDATE locales_npc_text SET entry = 12314, Text0_0_loc2 = null, Text0_1_loc2 = '« L\'Aigle de fer ». Mouais. C\'est un navire de transport, pas un zeppelin de chasse ! Mais bon, il n\'y a pas de sot métier, et tant que le prince me paye, Bomboum-je-me-la-pète peut bien raconter ce ce qu\'il veut.$b$bQuoi qu\'il en soit, bienvenue à bord.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12314;
UPDATE locales_npc_text SET entry = 12315, Text0_0_loc2 = 'Chuis occupé.', Text0_1_loc2 = 'Chuis occupée.', Text1_0_loc2 = 'Quoi ? Je suis occupé.', Text1_1_loc2 = 'Quoi ? Je suis occupée.', Text2_0_loc2 = 'En service. DU. BALAI.', Text2_1_loc2 = 'En service. DU. BALAI.', Text3_0_loc2 = 'Un truc à réparer ? Utilisez le Vend-O-Tron D-Luxe.', Text3_1_loc2 = 'Un truc à réparer ? Utilisez le Vend-O-Tron D-Luxe.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12315;
UPDATE locales_npc_text SET entry = 12316, Text0_0_loc2 = 'Nous protégeons ce vaisseau sur la route entre Orgrimmar et Grom\'gol dans la vallée de Strangleronce.', Text0_1_loc2 = 'Nous protégeons ce vaisseau sur la route entre Orgrimmar et Grom\'gol dans la vallée de Strangleronce.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12316;
