UPDATE locales_npc_text SET entry = 12318, Text0_0_loc2 = 'Je dirige le vaisseau le plus affûté d\'Azeroth. À l\'époque, il était équipé de deux canons légers jumelés et d\'assez de bombes pour ratiboiser une ville, vous savez… Son ombre suffisait à mettre une armée en déroute.$B$BQuoi ? Bien sûr que c\'est vrai !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12318;
