UPDATE locales_npc_text SET entry = 12323, Text0_0_loc2 = null, Text0_1_loc2 = 'Ce monument n\'est-il pas beau ? Il est important de nous rappeler les moments de notre histoire où ont été consentis de grands sacrifices, si nous ne voulons pas oublier le prix de toute guerre. Même des guerres gagnées.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12323;
UPDATE locales_npc_text SET entry = 12327, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à bord, $c. Mettez-vous à l\'aise et profitez du voyage. À bord de la Princesse violette, nous voulons que vous vous relaxiez. Nous avons des distributeurs automatiques en bas, si vous avez besoin de quoi que ce soit pendant la traversée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12327;
UPDATE locales_npc_text SET entry = 12328, Text0_0_loc2 = 'La relaxation, c\'est la clé de l\'efficacité d\'un équipage. Laissez votre équipage se détendre, et il tournera mieux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12328;
UPDATE locales_npc_text SET entry = 12329, Text0_0_loc2 = 'Le capitaine est génial ! Quand on arrive à quai, on a du temps pour se relaxer. C\'est le moment que j\'attends le plus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12329;
