UPDATE locales_npc_text SET entry = 12331, Text0_0_loc2 = 'Laisse-moi dormir cinq minutes de plus, $gfrangin:frangine;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12331;
UPDATE locales_npc_text SET entry = 12332, Text0_0_loc2 = 'Je suis occupé, là.', Text0_1_loc2 = 'Je suis occupé, là.', Text1_0_loc2 = 'Un truc à réparer ? Utilisez le Vend-O-Tron.', Text1_1_loc2 = 'Un truc à réparer ? Utilisez le Vend-O-Tron.', Text2_0_loc2 = 'Je n\'ai pas le temps de bavarder, là.', Text2_1_loc2 = 'Je n\'ai pas le temps de bavarder, là.', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12332;
UPDATE locales_npc_text SET entry = 12333, Text0_0_loc2 = null, Text0_1_loc2 = 'Le vol de Grom\'gol à Fossoyeuse est beau, bien que calme. Mais ce n\'est pas une excuse pour s\'endormir à son poste.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12333;
UPDATE locales_npc_text SET entry = 12334, Text0_0_loc2 = 'Venez vous mettre à l\'abri de la cendre et de la boue. Prenez un moment pour sécher.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12334;
UPDATE locales_npc_text SET entry = 12335, Text0_0_loc2 = 'Les L70ETC séjournent à Lune-d\'argent pendant que j\'organise leur prochaine tournée. Ils flânent souvent sur le balcon au-dessus quand ils ne sont pas en répétition. N\'hésitez pas à leur faire signe… ils adorent qu\'on s\'intéresse à eux.$B$BPour l\'instant, nous n\'engageons pas de technicien pour la tournée ou la scène, mais vous pouvez toujours revenir voir plus tard, qui sait ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12335;
UPDATE locales_npc_text SET entry = 12336, Text0_0_loc2 = 'Vous avez vu leur concert dans la forêt de Terokkar ! C\'était une tuerie !', Text0_1_loc2 = 'Vous avez vu leur concert dans la forêt de Terokkar ! C\'était une tuerie !', Text1_0_loc2 = 'Les L70ETC doivent être les ménestrels les plus connus de tout Azeroth !', Text1_1_loc2 = 'Les L70ETC doivent être les ménestrels les plus connus de tout Azeroth !', Text2_0_loc2 = 'Je me demande combien de temps ils resteront à Lune-d\'argent. J\'espère qu\'ils feront un concert !', Text2_1_loc2 = 'Je me demande combien de temps ils resteront à Lune-d\'argent. J\'espère qu\'ils feront un concert !', Text3_0_loc2 = 'Je donnerais quasiment n\'importe quoi pour avoir l\'occasion d\'aller là-haut pour leur parler…', Text3_1_loc2 = 'Je donnerais quasiment n\'importe quoi pour avoir l\'occasion d\'aller là-haut pour leur parler…', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12336;
UPDATE locales_npc_text SET entry = 12338, Text0_0_loc2 = null, Text0_1_loc2 = 'Ne combattez pas l\'ennemi sans y être $gpréparé:préparée;, $N. Je peux vous fournir des objets très puissants… à condition que vous vous soyez $grendu:rendue; utile.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12338;
