UPDATE locales_npc_text SET entry = 12340, Text0_0_loc2 = 'Les Confins du soleil sont à nous. Les chiens de Kael sont en fuite… Il est temps de mettre fin à leurs souffrances !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12340;
UPDATE locales_npc_text SET entry = 12341, Text0_0_loc2 = 'Je dirige les techniciens qui s\'occupent des L70ETC.$B$BVous savez : plier le matos, conduire les chariots, déplier le matos, monter et démonter la scène, mettre les effets pyrotechniques en place, tous ces genres de choses.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12341;
UPDATE locales_npc_text SET entry = 12342, Text0_0_loc2 = 'Schweitzer est un vieil ami à moi… c\'est précieux, ça. L\'amitié, je veux dire. Je pensais avoir oublié ce que c\'était, avant de retomber sur lui à Shattrath.$B$BIl ne passe plus souvent à Tirisfal ces temps-ci, alors je me suis dit que j\'allais prendre l\'orbe de transposition pour venir dire bonjour.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12342;
UPDATE locales_npc_text SET entry = 12343, Text0_0_loc2 = 'Mmrrglglglg ? Glbmr brmmrggl glmrrbmrgl.$B$BGrglgrlbrr mrgl !!!', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12343;
UPDATE locales_npc_text SET entry = 12344, Text0_0_loc2 = '<Ahlurglgr a l\'air surpris de vous comprendre.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12344;
UPDATE locales_npc_text SET entry = 12347, Text0_0_loc2 = 'La meilleure défense, c\'est l\'attaque, tout le monde vous le dira.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12347;
UPDATE locales_npc_text SET entry = 12348, Text0_0_loc2 = null, Text0_1_loc2 = 'Une arme solide ne vous servira à rien si vous ne vivez pas assez longtemps pour vous en servir, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12348;
UPDATE locales_npc_text SET entry = 12349, Text0_0_loc2 = 'Gawurgggl ! Blrgl-brgl grgl !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12349;
UPDATE locales_npc_text SET entry = 12350, Text0_0_loc2 = 'Vous avez appris un truc pour nous parler, $r, mais ça ne m\'impressionne pas.$B$BIl y a des traîtres aileron-d\'hiver sur l\'autre rive qu\'il faut tuer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12350;
UPDATE locales_npc_text SET entry = 12352, Text0_0_loc2 = 'Je ne suis pas aveugle, vous savez. Je vois bien ce qui se passe par ici !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12352;
UPDATE locales_npc_text SET entry = 12354, Text0_0_loc2 = 'Ahglrglglrglgrrr !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12354;
UPDATE locales_npc_text SET entry = 12357, Text0_0_loc2 = 'Je suis arrivé ici récemment, pour observer les murlocs aileron-d\'hiver. Je me suis déguisé en l\'un d\'entre eux et je me suis tenu à distance pour prendre des notes sur leur vie quotidienne.$B$BCependant, peu de temps après mon arrivée, tout cela a changé.$B$BUn tremblement de terre à Frimarra a provoqué un glissement de terrain qui a mis à jour les grottes non loin d\'ici. De ces cavernes est sorti un étrange makrura qui s\'est enfui avec les têtards des Ailerons-d\'hiver. Quand les murlocs se sont aventurés dans les grottes pour sauver leurs progénitures, ils en sont ressortis tout brillants et ensorcelés par le makrura.$B$BMaintenant, ceux qui ne sont pas touchés se sont repliés jusqu\'ici, et j\'en suis devenu le chef à contrecœur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12357;
UPDATE locales_npc_text SET entry = 12360, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12360;
UPDATE locales_npc_text SET entry = 12361, Text0_0_loc2 = 'Mes frères et sœurs sont devenus fous ! Vous pensez que vous pourriez m\'aider à sortir d\'ici ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12361;
UPDATE locales_npc_text SET entry = 12362, Text0_0_loc2 = 'Glug bmrgl grggl !', Text0_1_loc2 = null, Text1_0_loc2 = 'Mawurrgggl, gurgl lur !', Text1_1_loc2 = null, Text2_0_loc2 = 'Glumrggl ?', Text2_1_loc2 = null, Text3_0_loc2 = 'Brggl-brggl Mrgl-Mrgl ! Gluk !', Text3_1_loc2 = null, Text4_0_loc2 = 'Blrmrrgl gulrgl grr.', Text4_1_loc2 = null, Text5_0_loc2 = 'RAWGRLRLRLRRLGLRL !!!', Text5_1_loc2 = null, Text6_0_loc2 = 'Mrglrglrglrgl... glrgl ?', Text6_1_loc2 = null, Text7_0_loc2 = 'Mrglmrgl Glrggl !', Text7_1_loc2 = null WHERE entry = 12362;
UPDATE locales_npc_text SET entry = 12363, Text0_0_loc2 = 'Pas de temps pour discuter - j\'ai du travail !', Text0_1_loc2 = null, Text1_0_loc2 = 'Vous me comprenez ? Je vous comprends !', Text1_1_loc2 = null, Text2_0_loc2 = '$R, pourquoi est-ce que j\'aurais envie de vous parler, au juste ? C\'est sans doute les gens de votre race qui ont causé toute cette pagaille !', Text2_1_loc2 = null, Text3_0_loc2 = 'Il y a des palourdes à ramasser et des épaulards à tuer. Aidez-moi, ou dégagez d\'entre mes pattes !', Text3_1_loc2 = null, Text4_0_loc2 = 'Blurglgr murbrgl... Je plaisante, je vous comprends très bien.', Text4_1_loc2 = null, Text5_0_loc2 = 'Vous avez découvert pourquoi nos frères et sœurs du village sont devenus fous ?', Text5_1_loc2 = null, Text6_0_loc2 = 'Je savais bien que nous n\'aurions jamais dû aller dans ces cavernes. Il s\'y passe quelque chose de pas naturel !', Text6_1_loc2 = null, Text7_0_loc2 = 'Il y a quelque chose de bizarre chez le nouveau roi Mrgl-Mrgl. Je n\'arrive pas à mettre la nageoire dessus, mais il y a anguille sous roche. Il a beau noyer le poisson, je sens qu\'il nage en eaux troubles.', Text7_1_loc2 = null WHERE entry = 12363;
