UPDATE locales_npc_text SET entry = 12371, Text0_0_loc2 = '<Le faucon-dragon semble perturbé par l\'agitation qui règne tout autour.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12371;
UPDATE locales_npc_text SET entry = 12373, Text0_0_loc2 = 'Raser Orgrimmar n\'était pas seulement un assaut contre les orcs, c\'était un affront envers toute la Horde. Une attaque contre notre existence même !$B$BLe roi-liche cherche à instiller la peur…$B$BEt ceci…$B$B<Garosh désigne les murs fortifiés qui l\'entourent.>$B$BCeci est le résultat de la campagne d\'Arthas.$B$BLa Horde ne connaît pas la peur...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12373;
