UPDATE locales_npc_text SET entry = 12376, Text0_0_loc2 = 'Tout le monde trouve son bonheur à la fête du Feu du solstice d\'été !', Text0_1_loc2 = 'Tout le monde trouve son bonheur à la fête du Feu du solstice d\'été !', Text1_0_loc2 = 'Partout sur les terres, nos feux brûlent avec force et pureté !', Text1_1_loc2 = 'Partout sur les terres, nos feux brûlent avec force et pureté !', Text2_0_loc2 = 'Êtes-vous déjà $gallé:allée; danser au mât enrubanné ?', Text2_1_loc2 = 'Êtes-vous déjà $gallé:allée; danser au mât enrubanné ?', Text3_0_loc2 = 'J\'admire le talent des jongleurs de torches.', Text3_1_loc2 = 'J\'admire le talent des jongleurs de torches.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12376;
