UPDATE locales_npc_text SET entry = 12381, Text0_0_loc2 = 'La peur a toujours été notre meilleure arme. Des guerres entières se sont gagnées ou perdues sur la planche savonneuse de l\'affolement et du doute. Dites-moi, alors, comment affronter un ennemi qui ne connaît pas la peur ?$B$B<Saurcroc secoue la tête.>$B$BNous ne faisons pas que combattre le Fléau en Norfendre, nous combattons aussi pour reprendre le contrôle de nos esprits avant qu\'ils ne nous déchirent...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12381;
UPDATE locales_npc_text SET entry = 12382, Text0_0_loc2 = 'Il faut que cet avant-poste soit prêt pour notre offensive dans le bassin de Sholazar.$B$BNe restez pas $gplanté:plantée; là, $c. Allez défendre la cause de la Horde, par l\'honneur et le sang !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12382;
UPDATE locales_npc_text SET entry = 12384, Text0_0_loc2 = null, Text0_1_loc2 = 'Il faut que je nettoie ces cochonneries, mais je le ferai sans me plaindre.$B$BSi vous avez ne serait-ce qu\'une trace de la fierté de la Horde, aidez-moi !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12384;
UPDATE locales_npc_text SET entry = 12387, Text0_0_loc2 = null, Text0_1_loc2 = 'Les esprits s\'expriment tout autour de nous. Les écoutez-vous, $r ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12387;
UPDATE locales_npc_text SET entry = 12388, Text0_0_loc2 = 'Ici gît le cadavre de l\'un des gardes de la caravane de Mâchoire-d\'acier.', Text0_1_loc2 = 'Ici gît le cadavre de l\'un des gardes de la caravane de Mâchoire-d\'acier.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12388;
UPDATE locales_npc_text SET entry = 12389, Text0_0_loc2 = 'Ici gît le cadavre de l\'un des ouvriers de la caravane de Mâchoire-d\'acier.', Text0_1_loc2 = 'Ici gît le cadavre de l\'un des ouvriers de la caravane de Mâchoire-d\'acier.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12389;
UPDATE locales_npc_text SET entry = 12390, Text0_0_loc2 = 'Le Culte du crépuscule cherche à réduire à néant tout ce pour quoi nous nous battons. Il est de notre devoir d\'empêcher cette calamité, $r. Nous l\'avons juré.', Text0_1_loc2 = 'Le Culte du crépuscule cherche à réduire à néant tout ce pour quoi nous nous battons. Il est de notre devoir d\'empêcher cette calamité, $r. Nous l\'avons juré.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12390;
UPDATE locales_npc_text SET entry = 12394, Text0_0_loc2 = 'Je suis déshonoré. J\'aurais dû mourir à Magmoth, avec le long-voyant.$B$BLaissez-moi seul avec mes pensées.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12394;
UPDATE locales_npc_text SET entry = 12395, Text0_0_loc2 = 'L\'air élémentaire est tourmenté, ici. Attention… Ils ne vous laisseront pas passer $gsain et sauf:saine et sauve;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12395;
UPDATE locales_npc_text SET entry = 12403, Text0_0_loc2 = 'Voir le repos de mes ancêtres dérangé me chagrine.$B$BNous devons abattre l\'esprit maudit de votre commandant de corps Mâchoire-d\'acier, cet idiot qui a fait traverser cette terre sacrée à cette caravane.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12403;
UPDATE locales_npc_text SET entry = 12413, Text0_0_loc2 = 'Vous $gseul:seule; pouvez me sauver de Kaganishu, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12413;
UPDATE locales_npc_text SET entry = 12417, Text0_0_loc2 = 'Nous avons perdu un grand nombre de nos coursiers du vent lors de missions de reconnaissance vers l\'ouest. Le Fléau traque impitoyablement toute personne qui pourrait se révéler être un espion.$B$BHeureusement, j\'ai trouvé une solution qui ne met que la vie du cavalier en danger. Nous avons un taux de survie de 30p', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12417;
UPDATE locales_npc_text SET entry = 12419, Text0_0_loc2 = 'Je suis déshonoré. J\'aurais dû mourir à Magmoth, avec le long-voyant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12419;
UPDATE locales_npc_text SET entry = 12422, Text0_0_loc2 = 'Le point d\'accostage de Garrosh et Pal\'ea ont été anéantis par la brume. Nous sommes les seuls qui sommes assez fous pour rester, $gmon pote:petite;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12422;
UPDATE locales_npc_text SET entry = 12423, Text0_0_loc2 = null, Text0_1_loc2 = 'Comment est-ce que je suis censée arriver à quoi que ce soit avec Spumelevier toujours sur le dos et sans mon équipement ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12423;
UPDATE locales_npc_text SET entry = 12424, Text0_0_loc2 = 'Ce que vous voyez le long de cette côte n\'est pas le fait du Fléau. En fait, nous pensons que le Fléau craint cette brume aussi, et que c\'est pour cela qu\'il ne s\'aventure pas ici.$B$BCar, voyez-vous, la brume est vivante. Derrière ce rideau éthéré, c\'est la mort qui se cache...$B$BSi vous la laissez vous entourer, vous disparaîtrez. La brume vous emportera !$B$BPersonne n\'est encore revenu vivant de la brume.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12424;
UPDATE locales_npc_text SET entry = 12426, Text0_0_loc2 = 'Depuis mon arrivée ici, j\'ai vu des horreurs qui n\'existaient jusque-là que dans mes pires cauchemars, mais rien n\'aurait pu me préparer à ce qui se trouve par-delà la brume.$B$B<Meuhtou hoche la tête.>$B$BDerrière ce brouillard, à quelques mètres de nous à peine, se trouvent des créatures assoiffées de sang connues sous le nom de kvaldirs. Personne n\'est jamais ressorti de ce brouillard, et peu nombreux sont ceux qui ont réussi à s\'échapper quand la brume a déferlé sur le point d\'accostage pour la première fois. Depuis, l\'accès à cette zone a été condamné par Hurlenfer. Si vous y entrez, c\'est à vos risques et périls...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12426;
UPDATE locales_npc_text SET entry = 12427, Text0_0_loc2 = null, Text0_1_loc2 = 'Je… êtes-vous…$B$B<Meuhtou frissonne.>$B$BÊtes-vous là pour me sauver ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12427;
UPDATE locales_npc_text SET entry = 12433, Text0_0_loc2 = 'Je n\'ai jamais été aussi heureux de voir quelqu\'un ! Je suis trop faible pour briser les maillons de cette chaîne.$B$BEh bien, ne restez pas $gplanté:plantée; là, libérez-moi !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12433;
UPDATE locales_npc_text SET entry = 12435, Text0_0_loc2 = 'J\'ai les mains un peu prises, là, $c.$B$BQue puis-je faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12435;
UPDATE locales_npc_text SET entry = 12440, Text0_0_loc2 = null, Text0_1_loc2 = 'Parlez lentement, mon enfant, je ne suis pas habituée à votre langue étrange.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12440;
UPDATE locales_npc_text SET entry = 12441, Text0_0_loc2 = null, Text0_1_loc2 = 'S\'il vous plaît, $r, parlez doucement. Hamat est vieille et a besoin de repos.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12441;
UPDATE locales_npc_text SET entry = 12456, Text0_0_loc2 = 'D\'accord, mais asseyez-vous, ça risque de prendre un peu de temps.$B$BQuand nous sommes arrivés dans ce maudit désert de glace, le général Arlos, du donjon de la Bravoure, nous a donné à moi et à ma bande de têtes brûlées une mission spéciale : établir une base aérienne avancée pour les opérations de l\'Alliance.$B$BCette piste d\'atterrissage en est le résultat. Je peux vous dire qu\'entre toutes les bêtes vicieuses qui traînent dans le coin et le sol gelé plus dur que de la pierre, ça n\'a pas été une partie de plaisir ! Mais nous avons réussi, et rapidement avec ça !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12456;
UPDATE locales_npc_text SET entry = 12457, Text0_0_loc2 = 'Et donc, nous avions besoin de charrier beaucoup de sable et d\'huile pour les machines. Les bassins et lacs que l\'on trouve dans les environs convenaient parfaitement.$B$BUne partie du sable que nous avons trouvé s\'est même révélé avoir des propriétés magiques... Vous avez dit « ouverture » ? Mais bon, c\'est une autre histoire.$B$BDonc, nous avons drainé la plus grande partie de l\'eau et construit la station de pompage en plein au milieu de tout ça. Tout se passait comme sur des roulettes jusqu\'au jour où le tuyau d\'aspiration principal s\'est retrouvé bouché.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12457;
UPDATE locales_npc_text SET entry = 12458, Text0_0_loc2 = 'Enfin, tout ça je l\'ai appris plus tard, parce que je n\'étais pas là-bas à l\'époque.$B$BEn examinant ce qui bloquait l\'aspiration, ils sont tombés sur les pièces détachées d\'un robot qui ressemblait à un gnome ! Évidemment, ces idiots ont bossé jour et nuit pour l\'assembler sans en parler à personne.$B$BC\'est à ce moment-là que nous avons perdu tout contact avec la station de pompage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12458;
UPDATE locales_npc_text SET entry = 12459, Text0_0_loc2 = 'Au bout de quelques jours de silence, j\'ai fini par envoyer un groupe pour voir ce qui se passait à la plate-forme. Ils ne sont jamais revenus.$B$BJ\'en ai envoyé un deuxième le jour suivant, pour le même résultat, et nous avons perdu deux ou trois machines volantes sur des reconnaissances aériennes.$B$BLà, j\'ai détaché quelqu\'un vers le sud pour trouver de l\'aide, et nous nous sommes cloîtrés ici pour nous préparer au pire. Nous avons passé tout notre temps à fabriquer armures, armes et robots pour pouvoir débarquer là-bas en force.$B$BQuand nous y sommes allés, il y a quelques jours, nous n\'avons pas pu en croire nos yeux !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12459;
UPDATE locales_npc_text SET entry = 12460, Text0_0_loc2 = 'Comme je le disais, ce que nous avons trouvé là-bas défiait tout explication.$B$BImpossible de trouver mes hommes, mais à leur place il y avait une véritable armée de robots et d\'androïdes qui vaquaient à leurs occupations !$B$BLes androïdes ressemblaient tous à des gnomes. Des gnomoïdes ! Et ils nous ont dit qu\'ils attendaient notre arrivée. En fait, à leur manière, ils réagissaient comme s\'ils nous connaissaient. Ils nous ont rapidement encerclés et conduits vers le sommet de la station de pompage.$B$BC\'est là que nous avons vu qui était leur chef et ce qu\'il faisait aux gnomes survivants !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12460;
UPDATE locales_npc_text SET entry = 12461, Text0_0_loc2 = 'Il se fait appeler le maître des rouages Mécazod. Quand nous sommes arrivés, il était en train de transformer les survivants en machines !$B$BIl nous a accueillis chaleureusement et nous a expliqué qu\'il était l\'un des premiers gnomes à avoir été créé par quelque chose qu\'il appelait « le Grand Architecte », un gardien titan de la légendaire ville d\'Ulduar.$B$BApparemment, le bouchon que mon équipe avait dégagé des tuyaux de pompage, c\'était lui. Il était resté bloqué il y a des milliers d\'années, suite à une panne. C\'était bien notre veine d\'avoir construit la station de pompage juste au-dessus de lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12461;
UPDATE locales_npc_text SET entry = 12462, Text0_0_loc2 = 'Nos mécaniciens l\'avaient réassemblé et ramené à la « vie ».$B$BAlors, pour les remercier, il comptait tous nous guérir de ce qu\'il appelait « la malédiction de la chair ».$B$BD\'après Mécazod, c\'est une maladie que finissent par attraper toutes les créations des titans ! En d\'autres mots, nous sommes tous censés avoir été des robots à l\'origine, et avec les millénaires nous nous transformons lentement en créatures de chair !$B$BOui, c\'est débile.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12462;
UPDATE locales_npc_text SET entry = 12463, Text0_0_loc2 = 'Pour moi, tout ça ne ressemblait pas vraiment à une « guérison », et je n\'allais pas rester planté là à écouter parler un robot maboul pendant qu\'il massacrait mes hommes ! $B$BNous avons déguerpi, et c\'est ce qu\'aurait fait tout gnome un tant soit peu sensé ! En fait, la plupart d\'entre nous ont tout simplement sauté de la plate-forme en parachute, mais bon, vous voyez.$B$BPeu d\'entre nous ont réussi à revenir jusqu\'ici, et nous manquons encore d\'effectifs pour nous occuper de tout, entre les magnataures au nord et le Fléau à l\'est. Sans compter que la Horde vient de s\'installer tranquillement au nord-ouest !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12463;
UPDATE locales_npc_text SET entry = 12464, Text0_0_loc2 = 'Voilà donc où nous en sommes, $N.$B$BNous ne pourrons jamais gérer tout ça seuls et, franchement, je me sens un peu dépassé. Voltij saura quoi faire, vous devriez aller la voir.$B$BJ\'espère juste que vous arriverez à nous sortir de ce bazar sans qu\'on y laisse des membres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12464;
UPDATE locales_npc_text SET entry = 12472, Text0_0_loc2 = 'Les actes de ces déserteurs nous font honte à tous. Une honte telle qu\'il faut la ressentir pour la comprendre. C\'est pourquoi Ug\'thor est ici… Le garçon doit apprendre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12472;
UPDATE locales_npc_text SET entry = 12475, Text0_0_loc2 = null, Text0_1_loc2 = 'Soyez $gprudent:prudente; par ici, $r. Les indigènes sont sans pitié.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12475;
UPDATE locales_npc_text SET entry = 12477, Text0_0_loc2 = 'La loi rohart exige la vengeance de mes proches… Ces meurtriers ont complètement brûlé Pal\'ea.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12477;
UPDATE locales_npc_text SET entry = 12478, Text0_0_loc2 = 'J\'ai soigné des paysans, des nobles, des fils de marchands et des mendiants. Permettez-moi de vous faire partager mon observation : quand nous regardons la mort en face, nous sommes tous les mêmes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12478;
UPDATE locales_npc_text SET entry = 12485, Text0_0_loc2 = 'Votre cause est-elle juste, et votre cœur pur ? Si oui, vous êtes $gle bienvenu:la bienvenue; ici, chez la société druidique de protection des animaux. Tant que vous ne vous égarez pas sur l\'obscur chemin de la dépravation et que vous ne faites pas de mal aux animaux qui acceptent de coexister avec nous sur leur terre, la SdPA vous considérera comme $gun allié:une alliée;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12485;
UPDATE locales_npc_text SET entry = 12489, Text0_0_loc2 = 'Ce pilote de reconnaissance n\'a pas survécu au crash de son escadron.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12489;
UPDATE locales_npc_text SET entry = 12494, Text0_0_loc2 = 'Alors, $N, quel objet vous intéresse ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12494;
UPDATE locales_npc_text SET entry = 12495, Text0_0_loc2 = 'Ah - raretés et étrangetés. Nos spécialités sont les objets les plus incroyables d\'Azeroth, pour les aventuriers avisés.$B$BSi vous venez me voir, vous devez déjà avoir une idée de ce que vous recherchez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12495;
