UPDATE locales_npc_text SET entry = 12506, Text0_0_loc2 = 'Voyez, voyez, les flammes de l\'été !', Text0_1_loc2 = 'Voyez, voyez, les flammes de l\'été !', Text1_0_loc2 = 'Attention, ne vous brûlez pas !', Text1_1_loc2 = 'Attention, ne vous brûlez pas !', Text2_0_loc2 = 'Alors, la fête du Feu vous plaît ?', Text2_1_loc2 = 'Alors, la fête du Feu vous plaît ?', Text3_0_loc2 = 'Bienvenue ! Avez-vous déjà rendu hommage à la flamme de cette région ?', Text3_1_loc2 = 'Bienvenue ! Avez-vous déjà rendu hommage à la flamme de cette région ?', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12506;
UPDATE locales_npc_text SET entry = 12507, Text0_0_loc2 = null, Text0_1_loc2 = 'Cette terre a été dégradée bien au-delà de ce que vos yeux peuvent vous montrer. Même le passage du temps paraît différent.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12507;
UPDATE locales_npc_text SET entry = 12510, Text0_0_loc2 = 'Les maîtres de guerre des arènes sont dans la Ville basse, au sud-est, non loin du mur.', Text0_1_loc2 = 'Les maîtres de guerre des arènes sont dans la Ville basse, au sud-est, non loin du mur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12510;
UPDATE locales_npc_text SET entry = 12511, Text0_0_loc2 = 'Même si l\'avancée de Nesingwary contre la vie sauvage du Norfendre a été stoppée, ce n\'est qu\'une question de temps avant qu\'il ne fasse une autre tentative. Nous devons rester vigilants, $N !$B$BPlus que tout, vous devez vous garder de succomber aux tentations qui croiseront votre route... Le démon essayera de vous charmer. Il vous faudra y résister !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12511;
