UPDATE locales_npc_text SET entry = 12521, Text0_0_loc2 = null, Text0_1_loc2 = 'Malheur à moi si je laisse ces maudites créatures du Fléau nous chasser d\'ici ! Nous autres colons, nous avons tout abandonné pour refaire nos vies en Norfendre.$B$BJe peux vous dire que nous allons nous battre bec et ongles.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12521;
