UPDATE locales_npc_text SET entry = 12553, Text0_0_loc2 = 'Je peux faire quelque chose pour vous ? Ma sélection est un brin limitée, certes, mais c\'était prévisible… vu les circonstances.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12553;
UPDATE locales_npc_text SET entry = 12554, Text0_0_loc2 = 'Hein ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12554;
UPDATE locales_npc_text SET entry = 12555, Text0_0_loc2 = 'Mourir pour sa patrie est le devoir d\'un soldat. Mais si trouver que cette guerre est une folie est un acte de trahison, alors je suis un traître.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12555;
UPDATE locales_npc_text SET entry = 12556, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis très occupée, $r. Laissez-moi tranquille, s\'il vous plaît.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12556;
UPDATE locales_npc_text SET entry = 12566, Text0_0_loc2 = 'Rassemblez vos esprits, $gmortel:mortelle;. Vous êtes sur la ligne de front d\'une guerre dont vous n\'avez jamais vu la pareille.$B$BIl va falloir être en pleine possession de vos facultés si vous voulez pouvoir servir à quelque chose.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12566;
