UPDATE locales_npc_text SET entry = 12575, Text0_0_loc2 = 'La terre connaît un grand bouleversement !$B$BLe Ténébreux est de retour à la tête de ses San\'layn, les dragons se déchirent dans la guerre du Nexus, et les vrykuls s\'éveillent.$B$BVos peuples traversent les mers du Nord sur des navires de bois pour livrer bataille, contre toutes ces menaces ainsi qu\'entre eux. Nous, Kalu\'aks, sommes pris au milieu de tout cela.$B$BEt maintenant, un grand mal s\'est abattu et la terre entre en éruption, débordant de toute la puissance du monde. Nous devons trouver un refuge où nous aurons une chance de survivre.$B$BCes deux ambassadeurs me donnent mal à la tête... Peut-être pourriez-vous aider mon peuple, $r ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12575;
UPDATE locales_npc_text SET entry = 12576, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis réellement ravie de vous revoir, $N.', Text1_0_loc2 = null, Text1_1_loc2 = '$N ! Je me demandais comment vous alliez ! Qu\'est-ce qui vous a $gretardé:retardée; ?', Text2_0_loc2 = null, Text2_1_loc2 = 'Je vous fais confiance pour prendre toutes les précautions nécessaires…$B$BL\'endroit est dangeureux, et si quelque chose vous arrivait, je ne pourrais pas me le pardonner.', Text3_0_loc2 = null, Text3_1_loc2 = 'Il faut nous dépêcher, $N. Les néfastes machinations de Malygos auront bientôt passé le point de non-retour.', Text4_0_loc2 = null, Text4_1_loc2 = 'Par les temps qui courent, un ami cher est quelque chose d\'inestimable.$B$BPortez-vous bien, $N. Autant que faire se peut dans un endroit pareil…', Text5_0_loc2 = null, Text5_1_loc2 = 'Si vous ne m\'aviez pas libérée de cette prison, j\'aurais sûrement succombé à l\'heure qu\'il est.$B$BJe ne l\'oublie pas…', Text6_0_loc2 = null, Text6_1_loc2 = 'Quand nous aurons fait notre devoir ici, je trouverai un moyen de vous remercier de m\'avoir sauvée.', Text7_0_loc2 = null, Text7_1_loc2 = 'Mes forces me reviennent, doucement…$B$BJ\'espère pouvoir vous montrer ma vraie forme dans toute sa splendeur, bientôt.' WHERE entry = 12576;
UPDATE locales_npc_text SET entry = 12577, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh…$b$bOù suis-je ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12577;
UPDATE locales_npc_text SET entry = 12578, Text0_0_loc2 = 'Les kvaldirs ignorent la pitié, $N. Mais je ne peux pas abandonner le village sacré de Kaskala. Si nous ne fuyons pas, les esprits ne nous abandonneront pas non plus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12578;
UPDATE locales_npc_text SET entry = 12579, Text0_0_loc2 = 'Kaskala est attaquée ! Nous devons repousser l\'ennemi en attendant de pouvoir nous enfuir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12579;
UPDATE locales_npc_text SET entry = 12580, Text0_0_loc2 = 'Maudits hommes-géants ! S\'ils veulent se battre, on va se battre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12580;
