UPDATE locales_npc_text SET entry = 12585, Text0_0_loc2 = 'Ce jeune rohart semble avoir été tué au combat plutôt que noyé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12585;
UPDATE locales_npc_text SET entry = 12587, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12587;
UPDATE locales_npc_text SET entry = 12590, Text0_0_loc2 = 'En un mot : de la magie.$B$BCes irresponsables de mortels, limités qu\'ils sont dans leur espérance de vie, ne pensent pas une seconde aux conséquences à long terme de leurs actions.$B$BIls ont puisé trop intensément dans les flux d\'énergie arcanique, et ont contrarié l\'équilibre du monde. Si nous laissons cela continuer, Azeroth sera bientôt annihilée.$B$BSur ce point, les deux camps sont d\'accord. Mais avant même qu\'un conseil ne puisse être désigné, Malygos a mis son propre plan en marche.$B$BNous n\'avons pas eu d\'autre choix que de nous opposer à lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12590;
UPDATE locales_npc_text SET entry = 12591, Text0_0_loc2 = 'Nous avançons, $r, mais beaucoup trop lentement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12591;
UPDATE locales_npc_text SET entry = 12592, Text0_0_loc2 = 'Il faut que nous réussissions ! Le destin d\'Azeroth est en jeu…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12592;
UPDATE locales_npc_text SET entry = 12593, Text0_0_loc2 = 'Malygos le Tisse-sort, seigneur de la Magie, maître du Vol draconique bleu. Il est l\'aspect draconique bleu, l\'une des plus anciennes créatures d\'Azeroth.$B$BOn dit qu\'il revient de folie, une folie qui aurait duré des millénaires. Mais ses actes récents me laissent à penser qu\'il est encore loin d\'être sain d\'esprit.$B$BLa solution qu\'il a trouvée à notre dilemme est de traquer et éliminer tous les mortels d\'Azeroth qui font appel à la magie.$B$BLe Vol rouge, en tant que gardiens de la vie, a été forcé d\'intervenir.$B$BDésormais, les dragons combattent les dragons, et le destin de notre fragile monde est suspendu à cette guerre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12593;
UPDATE locales_npc_text SET entry = 12596, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis heureuse de vous voir ici, $c. La situation militaire sur le plateau du Puits de soleil est assez mal engagée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12596;
UPDATE locales_npc_text SET entry = 12597, Text0_0_loc2 = 'Le capitaine Selana m\'a ordonné de vous aider dans vos efforts de conquête du plateau du Puits de soleil.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12597;
UPDATE locales_npc_text SET entry = 12598, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'opération Soleil brisé a réussi une très belle avancée dans les zones extérieures de l\'île de Quel\'Danas. Nous repoussons les troupes de Kil\'jaeden et, dans la confusion générale, j\'ai pu mener un petit groupe d\'éclaireurs jusqu\'ici, sur le plateau du Puits de soleil. Mes troupes ont traversé la place Parhélion, ont repéré un groupe d\'elfes de sang lamesoleils, et nous avons donné l\'assaut. Ce combat s\'est vite révélé désastreux.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12598;
UPDATE locales_npc_text SET entry = 12599, Text0_0_loc2 = null, Text0_1_loc2 = 'Les défenseurs de Kil\'jaeden en poste ici sur le plateau du Puits de soleil sont bien plus puissants que ceux auxquels nous avons eu affaire à la périphérie de l\'île. La Légion a même corrompu nos gardiens arcaniques avec une énergie démoniaque pour en faire des créatures horribles. Il a suffi d\'un seul protecteur lamesoleil pour décimer mes hommes, et j\'ai rapidement sonné la retraite. Je m\'attendais à ce qu\'ils nous repoussent complètement en dehors du plateau, mais à ma grande surprise l\'ennemi a abandonné la poursuite.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12599;
UPDATE locales_npc_text SET entry = 12600, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne sais pas, mais c\'est une bonne chose. Cet instant de répit a permis à l\'anachorète Elbadon de sauver beaucoup de mes hommes. J\'ai envoyé quelques-uns de mes éclaireurs les plus discrets en reconnaissance, et ils m\'ont rapporté une information intéressante : quasiment tous les protecteurs lamesoleils sont désactivés ! Les troupes de Kil\'jaeden semblent économiser leur énergie, et se contentent de contenir notre avancée avec un seul protecteur actif. Si vous et vos compagnons voulez vous frayer un accès, laissez-moi vous suggérer un plan d\'attaque.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12600;
UPDATE locales_npc_text SET entry = 12601, Text0_0_loc2 = null, Text0_1_loc2 = 'Profitez du fait que les protecteurs sont désactivés. Si l\'un des éclaireurs lamesoleils vous repère, je suis sûre qu\'il essayera d\'activer le protecteur le plus proche. Tuez-le rapidement pour l\'en empêcher. Une fois les elfes de sang éliminés, tant que vous ne passerez pas à portée des capteurs de proximité des protecteurs, vous devriez pouvoir passer sans devoir livrer bataille. Cela vous fera gagner à la fois du temps et des ressources.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12601;
UPDATE locales_npc_text SET entry = 12604, Text0_0_loc2 = 'Toutes les portes qui barraient l\'accès au Puits de soleil sont détruites. Rendez-vous là-bas et empêchez Kil\'jaeden de pénétrer dans ce monde !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12604;
