UPDATE locales_npc_text SET entry = 12609, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12609;
UPDATE locales_npc_text SET entry = 12610, Text0_0_loc2 = 'Bienvenue dans notre camp, $c. Vous pouvez vous reposer un peu ici, si vous voulez.', Text0_1_loc2 = 'Bienvenue dans notre camp, $c. Vous pouvez vous reposer un peu ici, si vous voulez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12610;
UPDATE locales_npc_text SET entry = 12611, Text0_0_loc2 = 'Il n\'y a plus rien pour nous ici.', Text0_1_loc2 = 'Il n\'y a plus rien pour nous ici.', Text1_0_loc2 = 'Ma famille a été massacrée impitoyablement. Même les enfants.', Text1_1_loc2 = 'Ma famille a été massacrée impitoyablement. Même les enfants.', Text2_0_loc2 = 'Que le roi-liche brûle dans les flammes de l\'enfer pour la malédiction qu\'il a apportée à cette terre.', Text2_1_loc2 = 'Que le roi-liche brûle dans les flammes de l\'enfer pour la malédiction qu\'il a apportée à cette terre.', Text3_0_loc2 = 'Nous sommes un peuple sans foyer, maintenant.', Text3_1_loc2 = 'Nous sommes un peuple sans foyer, maintenant.', Text4_0_loc2 = 'Le Fléau est une machine qui ne connaît pas la peur dont le but est d\'exterminer toute vie sur cette terre.', Text4_1_loc2 = 'Le Fléau est une machine qui ne connaît pas la peur dont le but est d\'exterminer toute vie sur cette terre.', Text5_0_loc2 = 'Celui qu\'on appelle Hurlenfer est peut-être bien notre seul espoir.', Text5_1_loc2 = 'Celui qu\'on appelle Hurlenfer est peut-être bien notre seul espoir.', Text6_0_loc2 = 'Désormais, seule la Horde peut nous sauver.', Text6_1_loc2 = 'Désormais, seule la Horde peut nous sauver.', Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12611;
UPDATE locales_npc_text SET entry = 12617, Text0_0_loc2 = '<Budd se tient complètement immobile et fait semblant de ne pas vous entendre. En y regardant de très près, vous voyez un petit filet de bave couler de sous son masque.>', Text0_1_loc2 = null, Text1_0_loc2 = '<Budd se balance d\'avant en arrière sur les fesses, en murmurant quelque chose qui ressemble à du troll.>', Text1_1_loc2 = null, Text2_0_loc2 = 'Partez.', Text2_1_loc2 = null, Text3_0_loc2 = 'J\'ai pas le temps de bavasser, mec. J\'ai besoin de me w\'eposer.', Text3_1_loc2 = null, Text4_0_loc2 = 'Vive les tw\'olls, mec !', Text4_1_loc2 = null, Text5_0_loc2 = 'Mais caisse qui s\'passe, mec ? Jamais vu de tw\'oll ?', Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12617;
UPDATE locales_npc_text SET entry = 12618, Text0_0_loc2 = 'La Horde me donnera-t-elle une chance de combattre le Fléau ?', Text0_1_loc2 = 'La Horde me donnera-t-elle une chance de combattre le Fléau ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12618;
UPDATE locales_npc_text SET entry = 12619, Text0_0_loc2 = 'Pour venger les miens, pour repousser cette calamité qui a englouti notre terre, je vais prêter votre serment. Je vouerai tout ce que j\'ai et tout ce que je suis à la Horde.', Text0_1_loc2 = 'Pour venger les miens, pour repousser cette calamité qui a englouti notre terre, je vais prêter votre serment. Je vouerai tout ce que j\'ai et tout ce que je suis à la Horde.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12619;
UPDATE locales_npc_text SET entry = 12620, Text0_0_loc2 = 'Lok\'tar ogar ! La victoire ou la mort. Ces mots me lient à la Horde, car ils sont pour tout guerrier de la Horde la vérité la plus profonde et la plus sacrée qui soit.$B$BJe donne librement ma chair et mon sang au Chef de guerre. Je suis l’instrument de la volonté du Chef de guerre. Je suis une arme que brandit à sa guise le Chef de guerre.$B$BDès cet instant et jusqu’à la fin de mes jours, je vis et je meurs... POUR LA HORDE !', Text0_1_loc2 = 'Lok\'tar ogar ! La victoire ou la mort. Ces mots me lient à la Horde, car ils sont pour tout guerrier de la Horde la vérité la plus profonde et la plus sacrée qui soit.$B$BJe donne librement ma chair et mon sang au Chef de guerre. Je suis l’instrument de la volonté du Chef de guerre. Je suis une arme que brandit à sa guise le Chef de guerre.$B$BDès cet instant et jusqu’à la fin de mes jours, je vis et je meurs... POUR LA HORDE !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12620;
UPDATE locales_npc_text SET entry = 12622, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12622;
UPDATE locales_npc_text SET entry = 12623, Text0_0_loc2 = 'Les trois portes du Puits de soleil ont été abattues, $N.$B$BVous devez rallier vos troupes et empêcher Kil\'jaeden de pénétrer en Azeroth avant que tout ne soit perdu !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12623;
UPDATE locales_npc_text SET entry = 12626, Text0_0_loc2 = null, Text0_1_loc2 = 'Les Courcinglants ne retrouveront jamais leur gloire passée, mais ces ordures sur leurs barques paieront pour leurs actes - je le jure !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12626;
UPDATE locales_npc_text SET entry = 12627, Text0_0_loc2 = null, Text0_1_loc2 = 'Notre devoir est de protéger ces taunkas et de leur apporter le peu de réconfort que nous pourrons leur donner.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12627;
UPDATE locales_npc_text SET entry = 12634, Text0_0_loc2 = 'Caisse tu w\'egaw\'des comme ça, moussaillon ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12634;
UPDATE locales_npc_text SET entry = 12636, Text0_0_loc2 = 'J\'ai jamais compris pourquoi certains pirates enterrent leur trésor. Moi… J\'aime bien le garder à portée de main.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12636;
UPDATE locales_npc_text SET entry = 12637, Text0_0_loc2 = 'La grande tortue de mer appelée la Marcheuse des flots fait la liaison entre ici et Port-Moa\'ki dans la Désolation des dragons.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12637;
