UPDATE locales_npc_text SET entry = 12640, Text0_0_loc2 = 'Port-Moa\'ki a la chance de posséder deux grandes tortues marines : la Marcheuse des flots et l\'Île verte. Elles nagent vers Unu\'pe et Kamagua.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12640;
UPDATE locales_npc_text SET entry = 12641, Text0_0_loc2 = null, Text0_1_loc2 = 'Z\'avez hâte d\'aller en Norfendre, hein ? Le Kraken, un bateau à vapeur, assure la liaison entre ici et le donjon de la Bravoure dans la toundra Boréenne.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12641;
UPDATE locales_npc_text SET entry = 12642, Text0_0_loc2 = 'Si vous voulez vous rendre en Norfendre, le brise-glace Lance du nord assure la liaison entre ici et Valgarde dans le Fjord hurlant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12642;
UPDATE locales_npc_text SET entry = 12644, Text0_0_loc2 = 'Salut, vous ! Alors, les Royaumes de l\'est vous manquent, hein ? Eh bien, le Kraken retourne à Hurlevent, si vous avez vraiment envie de rentrer. À moi aussi, un peu de soleil me ferait plaisir !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12644;
