UPDATE locales_npc_text SET entry = 12646, Text0_0_loc2 = 'Lut. Cherchez à rentrer dans le vieux monde ? Envolez-vous vers les clairières de Tirisfal, et avec classe, à bord du Frôle-nuage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12646;
UPDATE locales_npc_text SET entry = 12647, Text0_0_loc2 = 'Salut. Si vous voulez aller en Norfendre, il faut prendre le Grand vent. Il vous emmène directement au bastion Chanteguerre dans la toundra Boréenne.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12647;
UPDATE locales_npc_text SET entry = 12649, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Norfendre vous attend, $r. Le majestueux Frôle-nuage vous emmène par-delà l\'océan à l\'accostage de la Vengeance dans le Fjord hurlant.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12649;
UPDATE locales_npc_text SET entry = 12650, Text0_0_loc2 = 'On rentre à la maison, $c ? Le Grand vent va vous y emmener, si votre maison est en Kalimdor. Il va directement à Durotar.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12650;
UPDATE locales_npc_text SET entry = 12657, Text0_0_loc2 = 'Recherché MORT pour trahison et autres crimes contre la Horde !$B$BS\'adresser au capitaine Gort pour les récompenses.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12657;
