UPDATE locales_npc_text SET entry = 12659, Text0_0_loc2 = 'Bon vent, moussaillon ! Asseyez-vous et prenez donc une p\'tite goulée !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12659;
UPDATE locales_npc_text SET entry = 12663, Text0_0_loc2 = 'Mon seul souhait a toujours été de servir mon roi à nouveau. Pour l\'humanité, contre un ennemi commun.$B$BAu lieu de ça, je m\'aperçois que des ordures de sectateurs se sont infiltrés dans nos rangs… Et tous les signes indiquent Naxxanar comme origine de cette pourriture.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12663;
UPDATE locales_npc_text SET entry = 12664, Text0_0_loc2 = 'C\'est de là que viennent les ordres des sectateurs. Leur chef va forcément se montrer bientôt.$B$BNe laissons pas passer cette occasion.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12664;
UPDATE locales_npc_text SET entry = 12665, Text0_0_loc2 = 'Le sacrifice d\'Anveena nous a donné à tous un nouvel espoir pour l\'avenir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12665;
UPDATE locales_npc_text SET entry = 12666, Text0_0_loc2 = 'Hein ? Quoi ? Dégage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12666;
UPDATE locales_npc_text SET entry = 12667, Text0_0_loc2 = null, Text0_1_loc2 = 'Si j\'arrive à fabriquer une nouvelle machine de guerre, je sais que je pourrai en tirer une jolie somme au portail du Courroux. Certains de nos précédents prototypes n\'étaient pas du genre le plus… solide, mais cette machine volante devrait convenir, si je réussis à y ajouter un système de bombardement efficace.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12667;
UPDATE locales_npc_text SET entry = 12668, Text0_0_loc2 = 'Il y a de l\'argent à se faire ! Comme le disait papa : il n\'y a pas mieux qu\'une bonne guerre pour stimuler le commerce.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12668;
UPDATE locales_npc_text SET entry = 12669, Text0_0_loc2 = 'En vous tenant devant cette idole antique, vous sentez une étrange présence.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12669;
UPDATE locales_npc_text SET entry = 12670, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12670;
UPDATE locales_npc_text SET entry = 12673, Text0_0_loc2 = 'Bienvenue ! Si vous cherchez un endroit pour vous reposer, je peux vous aider.', Text0_1_loc2 = 'Bienvenue ! Si vous cherchez un endroit pour vous reposer, je peux vous aider.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12673;
UPDATE locales_npc_text SET entry = 12678, Text0_0_loc2 = 'Nous allons écraser Arthas ! Le Fléau ne peut pas s\'opposer à la puissance de la Horde !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12678;
UPDATE locales_npc_text SET entry = 12684, Text0_0_loc2 = 'Vous cherchez un maître de guerre de l\'arène ? Vous en trouverez sur la route qui relie les villes, ou dans les capitales.$B$BVous pouvez lier votre pierre de foyer auprès de moi. Je peux aussi vous téléporter vers l\'une des capitales.', Text0_1_loc2 = 'Vous cherchez un maître de guerre de l\'arène ? Vous en trouverez sur la route qui relie les villes, ou dans les capitales.$B$BVous pouvez lier votre pierre de foyer auprès de moi. Je peux aussi vous téléporter vers l\'une des capitales.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12684;
UPDATE locales_npc_text SET entry = 12687, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12687;
UPDATE locales_npc_text SET entry = 12694, Text0_0_loc2 = 'Vous feriez mieux d\'dégager d\'ici en vitesse, sauf si vous voulez aller faire un tour dans l\'une des cages…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12694;
UPDATE locales_npc_text SET entry = 12696, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12696;
UPDATE locales_npc_text SET entry = 12698, Text0_0_loc2 = 'Aidez-nous à repousser le Fléau, soldat ! Signalez toute activité suspecte ! On tue d\'abord, on pose les questions après !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12698;
UPDATE locales_npc_text SET entry = 12699, Text0_0_loc2 = 'Je vous jure, ces imbéciles de Réprouvés vont tous nous faire tuer !', Text0_1_loc2 = null, Text1_0_loc2 = 'Si vous voulez mon avis, et vous l\'avez demandé, je pense qu\'il faudrait zigouiller tous les apothicaires avant qu\'il ne soit trop tard !', Text1_1_loc2 = null, Text2_0_loc2 = 'La Horde, c\'est l\'honneur et la guerre. J\'ai la certitude qu\'il n\'y a rien d\'honorable à ce que préparent les apothicaires.', Text2_1_loc2 = null, Text3_0_loc2 = 'Je surveille ces idiots de réprouvés, là-bas. On ne se méfie jamais assez, avec eux !', Text3_1_loc2 = null, Text4_0_loc2 = 'Si vous avez affaire avec le docteur qui est là-bas, soyez sur vos gardes. Quoi qu\'ils préparent, ce n\'est pas naturel.', Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12699;
UPDATE locales_npc_text SET entry = 12701, Text0_0_loc2 = 'Hé mec, les steppes qui sont à l\'ouest, elles sont pleines de malfaisants ! Faut pas y aller, mec, si tu tiens à la vie !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12701;
UPDATE locales_npc_text SET entry = 12703, Text0_0_loc2 = 'Mes fonctions au Marteau d\'Agmar s\'étendent bien au-delà de la gestion des écuries.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12703;
UPDATE locales_npc_text SET entry = 12705, Text0_0_loc2 = 'Mort ? Moi ? Eh, loin de là, $gpetit:petite;. C\'était juste une égratignure.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12705;
UPDATE locales_npc_text SET entry = 12706, Text0_0_loc2 = 'Impressionnants, n\'est-ce pas ? Ces géants seront parfaits comme chair à can - comme alliés. Celui-là, là, c\'est leur chef. Mais il refuse toujours de me parler.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12706;
UPDATE locales_npc_text SET entry = 12707, Text0_0_loc2 = 'Bonjour, $r. Nous vous invitons à partager ce canyon avec nous, pour échapper aux dragons. Mais faites attention à ce gringalet de gobelin… On ne peut pas faire confiance aux gens de son espèce.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12707;
