UPDATE locales_npc_text SET entry = 12739, Text0_0_loc2 = 'Par-delà les vallées et pics du mont Hyjal, dans les sables ondoyants de Silithus, contre les infernales armées de la Légion - nous combattons. Nous sommes les fils et filles de l\'Alliance, sans nom et sans visage. Par la Lumière et par la force de l\'Alliance, le premier assaut nous appartient - et le dernier assaut clôt l\'horizon de nos ennemis.$B$BNous sommes 7e Légion.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12739;
