UPDATE locales_npc_text SET entry = 12776, Text0_0_loc2 = 'Puisse la Lumière vous purger de cette infection, brave $N.', Text0_1_loc2 = 'Puisse la Lumière vous purger de cette infection, brave $N.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12776;
UPDATE locales_npc_text SET entry = 12777, Text0_0_loc2 = 'Mon père m\'a fait don de son armure et de sa hache. Bientôt j\'en ferai bon usage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12777;
UPDATE locales_npc_text SET entry = 12779, Text0_0_loc2 = 'Heureusement, les toilettes sont libres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12779;
UPDATE locales_npc_text SET entry = 12780, Text0_0_loc2 = null, Text0_1_loc2 = 'Nos réserves de nourriture et d\'eau sont au plus bas. J\'ai bien peur de ne rien avoir à vous offrir.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12780;
UPDATE locales_npc_text SET entry = 12781, Text0_0_loc2 = null, Text0_1_loc2 = 'Merci pour votre aide, $N. Ces givrelles sont, littéralement, le fruit de votre labeur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12781;
UPDATE locales_npc_text SET entry = 12785, Text0_0_loc2 = 'Quand j\'ai appris que mes cousins vivaient ici dans le nord, je suis venu en apprendre plus. Ce que j\'y ai trouvé, c\'est des tribus belliqueuses qui vont à l\'opposé de notre nature profonde.$B$BJ\'ai perdu tout espoir, $r. Même le grand Ursoc a été souillé par cet endroit maudit.$B$BIl n\'y a plus d\'espoir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12785;
UPDATE locales_npc_text SET entry = 12787, Text0_0_loc2 = 'Oui… Je sens la magie de Vordrassil, libre de toute corruption.$B$BJe vous en prie, vous devez me laisser vous aider. C\'est le destin qui m\'a conduit ici, peut-être pourrai-je encore servir à quelque chose !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12787;
UPDATE locales_npc_text SET entry = 12789, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12789;
UPDATE locales_npc_text SET entry = 12790, Text0_0_loc2 = 'Zybarus vous remercie du fond du cœur, $gchampion:championne;. Sans vous, Zybarus serait mort. Ou, pire, Zybarus serait devenu l\'un d\'entre eux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12790;
UPDATE locales_npc_text SET entry = 12793, Text0_0_loc2 = 'Autrefois, les Grisonnes étaient mon territoire. J\'étais le fils du grand Ursoc, qui régnait sur cette terre.$B$BMais maintenant… Je suis condamné à passer le reste de ma vie dans cette carverne.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12793;
UPDATE locales_npc_text SET entry = 12794, Text0_0_loc2 = 'Vous ne comprenez pas. Si je le voulais, je pourrais me frayer un chemin hors de cette caverne.$B$BMais c\'est mon père, Ursoc… Il est revenu d\'entre les morts, mais… il n\'est plus le même.$B$BIl a sacrifié sa vie pendant la guerre des Anciens, il y a des milliers d\'années. Pendant des siècles, les furbolgs qui le vénéraient ont essayé de trouver un moyen de le ressusciter.$B$BIls ont fini par décider qu\'il fallait qu\'ils tentent de faire repousser l\'arbre-monde dégénéré, Vordrassil, afin d\'en utiliser la magie pour faire revenir mon père.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12794;
UPDATE locales_npc_text SET entry = 12795, Text0_0_loc2 = null, Text0_1_loc2 = 'Ne faites pas trop de bruit, $c. Je suis une espionne de Vexevenin infiltrée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12795;
UPDATE locales_npc_text SET entry = 12796, Text0_0_loc2 = 'L\'arbuste qu\'ils ont fait pousser a donné un fruit. Au début, tout a semblé marcher. Ursoc est effectivement revenu, et j\'ai été très heureux de son retour pendant quelque temps.$B$BMais au fur et à mesure qu\'il reprenait des forces, il est devenu clair qu\'il avait changé. Et les furbolgs aussi.$B$BLe mal, quel qu\'il puisse être, qui avait poussé les druides d\'autrefois à abattre Vordrassil, s\'était à nouveau incarné dans le nouvel arbre et les avait tous transformés en monstres assoiffés de sang.$B$BLa chose qui erre dans les Grisonnes à présent n\'est plus mon père. Il faut y mettre fin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12796;
UPDATE locales_npc_text SET entry = 12797, Text0_0_loc2 = 'Je suis Kodian, fille d\'Ursoc. Je veille sur ce sanctuaire, qui fut autrefois sacré.$B$BCela faisait très longtemps que je n\'avais pas vu un étranger ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12797;
UPDATE locales_npc_text SET entry = 12798, Text0_0_loc2 = 'Un furbolg du nom de Tur Ragepatte. Il n\'était pas l\'un des nôtres.$B$BLui venait des terres du sud, de la tribu des Grumegueules.$B$BIl voulait voir Ursoc. Il semblait penser que Vordrassil pouvait être purifié de sa corruption et servir à sauver mon père.$B$BEntretenir de tels espoirs est idiot. Vous serez d\'accord avez moi, quand vous aurez vu comme moi la noirceur qui s\'est emparée de lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12798;
UPDATE locales_npc_text SET entry = 12804, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous pensez que vous pourriez me faire sortir d\'ici, $c ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12804;
UPDATE locales_npc_text SET entry = 12805, Text0_0_loc2 = 'Oh, vous êtes là pour me secourir, bien. Maintenant, faites-moi sortir d\'ici !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12805;
UPDATE locales_npc_text SET entry = 12806, Text0_0_loc2 = 'Hé, salut $gmon gars:ma fille;. Ça fait plusieurs fois que je vous vois passer dans le coin.$B$BÇa vous dirait de me faire sortir d\'ici ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12806;
UPDATE locales_npc_text SET entry = 12808, Text0_0_loc2 = 'Je ne sais pas ce qui s\'est passé. Je venais d\'arriver à Vexevenin, et tout d\'un coup je me réveille dans cette cage !$B$BVous êtes là pour me sortir d\'ici ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12808;
UPDATE locales_npc_text SET entry = 12810, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $c.$B$BQue puis-je faire pour vous aider ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12810;
UPDATE locales_npc_text SET entry = 12811, Text0_0_loc2 = 'Le caractériel proto-drake du thane Torvald Eriksson est enchaîné ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12811;
UPDATE locales_npc_text SET entry = 12812, Text0_0_loc2 = 'Oui, qu\'est-ce que c\'est ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12812;
UPDATE locales_npc_text SET entry = 12819, Text0_0_loc2 = 'Tous les deux ans, les esprits de Compétition sont pris d\'une ferveur particulière… C\'est la saison ! Nous portons ce tabard sur les champs de bataille pour leur rendre hommage, et pour nous attirer leur bienveillance en écrasant nos ennemis.', Text0_1_loc2 = 'Tous les deux ans, les esprits de Compétition sont pris d\'une ferveur particulière… C\'est la saison ! Nous portons ce tabard sur les champs de bataille pour leur rendre hommage, et pour nous attirer leur bienveillance en écrasant nos ennemis.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12819;
UPDATE locales_npc_text SET entry = 12820, Text0_0_loc2 = 'Il suffit d\'affronter nos adversaires sur un champ de bataille. Que vous gagniez ou perdiez, si vous restez jusqu\'à la fin du combat, un tabard vous sera envoyé par courrier.', Text0_1_loc2 = 'Il suffit d\'affronter nos adversaires sur un champ de bataille. Que vous gagniez ou perdiez, si vous restez jusqu\'à la fin du combat, un tabard vous sera envoyé par courrier.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12820;
UPDATE locales_npc_text SET entry = 12821, Text0_0_loc2 = 'Vous devez vaincre nos adversaires sur un champ de bataille. À chaque victoire, vous aurez une chance de recevoir un médaillon en or par courrier.', Text0_1_loc2 = 'Vous devez vaincre nos adversaires sur un champ de bataille. À chaque victoire, vous aurez une chance de recevoir un médaillon en or par courrier.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12821;
UPDATE locales_npc_text SET entry = 12822, Text0_0_loc2 = 'Vous avez entendu parler des esprits de Compétition ?$B$BTous les deux ans, ils se rapprochent du royaume des mortels. Alors, les membres de l\'Alliance et de la Horde portent des tabards de compétition sur les champs de bataille pour leur rendre hommage, et chaque camp cherche à vaincre l\'autre pour s\'attirer leur bienveillance.$B$BJ\'entasse des souvenirs de ces périodes troublées. Si vous connaissez le mot de passe, je pourrais peut-être accepter de me séparer de l\'un d\'entre eux...', Text0_1_loc2 = 'Vous avez entendu parler des esprits de Compétition ?$B$BTous les deux ans, ils se rapprochent du royaume des mortels. Alors, les membres de l\'Alliance et de la Horde portent des tabards de compétition sur les champs de bataille pour leur rendre hommage, et chaque camp cherche à vaincre l\'autre pour s\'attirer leur bienveillance.$B$BJ\'entasse des souvenirs de ces périodes troublées. Si vous connaissez le mot de passe, je pourrais peut-être accepter de me séparer de l\'un d\'entre eux...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12822;
UPDATE locales_npc_text SET entry = 12823, Text0_0_loc2 = null, Text0_1_loc2 = 'Dans ce désert gelé, nous apprenons vite le prix de la mort, $N. Le pouvoir du roi-liche s\'étend sur chaque centimètre carré du Norfendre, il imprègne la terre elle-même ! Il retourne nos propres morts contre nous.$B$BDepuis le ciel, des chevaucheurs de griffons morts-vivants font tomber une pluie de marteaux-tempêtes, tandis que des cadavres ambulants nous poursuivent sur terre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12823;
UPDATE locales_npc_text SET entry = 12824, Text0_0_loc2 = 'Krenna doit douter de mes capacités. Pendant que nos combattants d\'élite sont en marche pour aller affronter l\'Alliance et les trappeurs, ou pour aller semer la dévastation chez les trolls, elle m\'a chargé du nettoyage de la vermine écorche-dragon.$B$BSi nous combinons nos efforts, nous pourrons peut-être tous les deux faire nos preuves à la conquérante.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12824;
