UPDATE locales_npc_text SET entry = 12831, Text0_0_loc2 = 'Tous les deux ans, les esprits de Compétition sont pris d\'une ferveur particulière… C\'est la saison ! Nous portons ce tabard sur les champs de bataille pour leur rendre hommage, et pour nous attirer leur bienveillance en écrasant nos ennemis.', Text0_1_loc2 = 'Tous les deux ans, les esprits de Compétition sont pris d\'une ferveur particulière… C\'est la saison ! Nous portons ce tabard sur les champs de bataille pour leur rendre hommage, et pour nous attirer leur bienveillance en écrasant nos ennemis.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12831;
