UPDATE locales_npc_text SET entry = 12842, Text0_0_loc2 = 'Quoi ? Vous voyez pas que je suis occupé ?$B$BVous croyez qu\'il va se réparer tout seul, le transmatière ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12842;
UPDATE locales_npc_text SET entry = 12843, Text0_0_loc2 = 'Le généralissime Bolvar Fordragon combat pour nous tous. S\'il n\'était pas là, Arthas serait tranquillement assis sur le trône de Hurlevent à l\'heure qu\'il est.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12843;
UPDATE locales_npc_text SET entry = 12844, Text0_0_loc2 = 'Je suis si près de la solution. Encore une minute et je mettrai le doigt dessus !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12844;
