UPDATE locales_npc_text SET entry = 12846, Text0_0_loc2 = 'Oui, $gmon fils:ma fille; ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12846;
UPDATE locales_npc_text SET entry = 12848, Text0_0_loc2 = '<Le grand abbé vous dévisage un moment avant de sourire.>$B$BTrès bien, mon enfant. Je connais un endroit. Embrassez mon anneau et dites-moi quand vous serez $gprêt:prête;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12848;
UPDATE locales_npc_text SET entry = 12849, Text0_0_loc2 = 'Alors rendez-vous à l\'entrée de l\'abbaye. Je vous y rejoins dans un petit instant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12849;
UPDATE locales_npc_text SET entry = 12850, Text0_0_loc2 = 'J\'ai participé à la destruction de civilisations entières… Devoir m\'abaisser à balayer ce misérable petit Culte des damnés me dégoûte.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12850;
UPDATE locales_npc_text SET entry = 12851, Text0_0_loc2 = null, Text0_1_loc2 = 'D\'accord, mais faites un peu attention cette fois !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12851;
UPDATE locales_npc_text SET entry = 12852, Text0_0_loc2 = null, Text0_1_loc2 = 'Ne parlez pas trop fort. Attirer l\'attention de ces grosses bébêtes ne serait pas une bonne idée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12852;
UPDATE locales_npc_text SET entry = 12853, Text0_0_loc2 = 'Ne m\'embêtez pas avec des broutilles !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12853;
UPDATE locales_npc_text SET entry = 12856, Text0_0_loc2 = 'Vous ne voyez pas que je suis occupé ? Ce camp ne sera jamais prêt à temps pour le retour du prince !', Text0_1_loc2 = null, Text1_0_loc2 = 'Si ces satanés animaux ne s\'étaient pas amusés à brûler nos navires, nous serions déjà à la maison. Et maintenant, laissez-moi tranquille, que je reprenne le boulot !', Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12856;
UPDATE locales_npc_text SET entry = 12857, Text0_0_loc2 = 'La Croisade d\'argent est venue en force, mon $gami:amie;. Nous n\'allions pas vous laisser toute la gloire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12857;
UPDATE locales_npc_text SET entry = 12858, Text0_0_loc2 = 'Il faut que ces satanés paysans travaillent plus vite ! Je veux rentrer à Forgefer. Ma femme et mes enfants me manquent vraiment.', Text0_1_loc2 = null, Text1_0_loc2 = 'Le cap\'taine dit que dès que le prince Arthas sera de retour, nous rentrerons à Lordaeron.', Text1_1_loc2 = null, Text2_0_loc2 = 'Je ne sais pas ce qui retarde autant le jeune prince, mais je ne crois pas que nous tiendrons encore très longtemps. Le froid commence à avoir raison de certains des soldats.', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12858;
UPDATE locales_npc_text SET entry = 12859, Text0_0_loc2 = 'Quand nous avons vu que ces bêtes sauvages avaient brûlé nos bateaux, nos espoirs se sont effondrés. Je ne m\'étais jamais senti aussi perdu.', Text0_1_loc2 = null, Text1_0_loc2 = 'La fureur. C\'est ce qui résume le mieux ce que m\'inspire toute cette pagaille. Nous nous sommes cassés le dos à essayer de traverser les bois jusqu\'à la rive, tout ça pour découvrir que nous arrivions trop tard. Notre seul moyen de rentrer chez nous avait été détruit.', Text1_1_loc2 = null, Text2_0_loc2 = 'Malgré le calvaire que nous avons vécu dans ce désert, je persiste à croire que le jeune prince nous mènera à la victoire. Toutes les nuits, je prie pour qu\'il reste sain et sauf.', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12859;
UPDATE locales_npc_text SET entry = 12860, Text0_0_loc2 = '<Le fantassin oublié secoue la tête.>$B$BNous ne pouvons plus tenir, mon $gami:amie;. Si le prince Arthas ne revient pas vite, nous allons tous y passer.', Text0_1_loc2 = null, Text1_0_loc2 = 'Ces bateaux ne seront jamais prêts à temps. L\'hiver arrive, et avec lui la mort. J\'ai bien peur que le jeune prince ne nous ait tous condamnés.', Text1_1_loc2 = null, Text2_0_loc2 = 'Cela fait longtemps qu\'Arthas et Muradin sont partis. J\'espère qu\'ils ne sont pas en danger.', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12860;
UPDATE locales_npc_text SET entry = 12862, Text0_0_loc2 = 'Où c\'est-y que j\'vous envoie, aujourd\'hui ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12862;
UPDATE locales_npc_text SET entry = 12863, Text0_0_loc2 = 'Un bon club peut faire toute la différence du monde.', Text0_1_loc2 = 'Un bon club peut faire toute la différence du monde.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12863;
