UPDATE locales_npc_text SET entry = 12865, Text0_0_loc2 = 'Salut, $r !$B$BSi vous cherchez un déchiqueteur à piloter, il va falloir vous diriger vers les collines juste au nord d\'ici.$B$BCelui-là, il est plus en service.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12865;
