UPDATE locales_npc_text SET entry = 12868, Text0_0_loc2 = 'Avant que les hostilités n\'éclatent, on les réparait pour découper les arbres du coin.$B$BMais depuis peu, ils ont été réquisitionnés pour combattre contre la Horde. Et en fait, ça fait d\'assez bonnes machines de guerre. Si vous avez une clé, il serait bien bête de ne pas vous en servir !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12868;
