UPDATE locales_npc_text SET entry = 12942, Text0_0_loc2 = null, Text0_1_loc2 = 'Est-ce que vous trouver en présence d\'un dragon noir vous met mal à l\'aise ?$B$BBien. C\'est ce qu\'il faut. Et ces autres dragons sont mal à l\'aise également. Mais ils s\'accrochent à leurs principes antiques, ils n\'oseraient pas m\'attaquer ici.$B$BLes imbéciles.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12942;
UPDATE locales_npc_text SET entry = 12943, Text0_0_loc2 = 'Écoutez, $r.$B$BSi vous cherchez un déchiqueteur à piloter, il va falloir vous diriger vers les collines juste au nord d\'ici.$B$BCelui-là, il est plus en service.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12943;
