UPDATE locales_npc_text SET entry = 12946, Text0_0_loc2 = 'Oui ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12946;
UPDATE locales_npc_text SET entry = 12949, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Vol infini tente de modifier le destin du prince Arthas Menethil. Pour vous, il est le roi-liche, mais ici et maintenant il est encore un prince de Lordaeron qui essaie de faire au mieux pour défendre son royaume. Aujourd\'hui, Arthas va effectuer un choix lourd de conséquences, qui mènera à ce que nous autres Gardiens du temps appelons un point d\'inflexion dans la voie temporelle.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12949;
UPDATE locales_npc_text SET entry = 12950, Text0_0_loc2 = null, Text0_1_loc2 = 'Arthas donne l\'ordre de purger Stratholme, en abattant jusqu\'aux derniers les humains qui habitent la ville. Il a découvert que le Fléau a infecté la ville en y introduisant sa peste. Et cette peste ne fait pas que tuer les gens : ceux qui en meurent se relèvent sous la forme de zombies décérébrés et partent alimenter la machine de guerre du Fléau.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12950;
UPDATE locales_npc_text SET entry = 12952, Text0_0_loc2 = null, Text0_1_loc2 = 'Les Infinis ont adopté une tactique inhabituellement subtile. Ils essaient de dissimuler les signes qui mèneront Arthas à sa décision de purger Stratholme. Leurs agents ont lancé des sortilèges d\'illusion sur les cargaisons de grain corrompu de la campagne environnante pour leur donner une apparence saine. Il faut que vous trouviez le grain caché et que vous utilisiez ce disrupteur arcanique sur les cargaisons. Les hommes d\'Arthas recherchent des céréales pestiférées, et ne devraient pas tarder à les découvrir une fois l\'illusion levée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12952;
UPDATE locales_npc_text SET entry = 12953, Text0_0_loc2 = null, Text0_1_loc2 = 'Je pense que nous ne pouvons pas nous permettre de laisser passer une occasion de nous attirer la gratitude du Vol rouge. Avec les batailles qui nous attendent, l\'avoir comme allié serait un avantage inestimable.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12953;
UPDATE locales_npc_text SET entry = 12954, Text0_0_loc2 = 'Par le passé, le démoniste Nekros Brise-tête a usurpé les pouvoirs d\'une puissante relique pour asservir Alexstrasza et une grande partie du Vol rouge. Zuluhed et Nekros sont morts, mais la dette que nous avons envers les rouges est loin d\'être remboursée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12954;
UPDATE locales_npc_text SET entry = 12955, Text0_0_loc2 = 'L\'Alliance tient ce gîte isolé depuis quelque temps. Nos chasseurs et forestiers l\'utilisent de temps en temps.$B$BMais maintenant que la guerre fait rage, les militaires en ont pris le contrôle, évidemment.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12955;
UPDATE locales_npc_text SET entry = 12958, Text0_0_loc2 = '<Le commandant interrompt sa leçon et se retourne. Ses yeux vous examinent, vous jaugent.>$B$BQuelqu\'un comme vous pourrait m\'être utile, $c.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12958;
UPDATE locales_npc_text SET entry = 12960, Text0_0_loc2 = 'Oui, $c, qu\'y a-t-il ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12960;
UPDATE locales_npc_text SET entry = 12961, Text0_0_loc2 = 'Je suis heureux de vous revoir, $N.$B$BVous venez vous remettre à niveau sur les tactiques du Fléau ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12961;
