UPDATE locales_npc_text SET entry = 12971, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12971;
UPDATE locales_npc_text SET entry = 12976, Text0_0_loc2 = null, Text0_1_loc2 = '$N, je suis contente de vous revoir !$B$BEuh, ou est-ce que c\'est la première fois qu\'on se rencontre ? $B$BJe suis tellement partout à la fois dans le temps et l\'espace en ce moment que j\'ai parfois un peu de mal à suivre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12976;
UPDATE locales_npc_text SET entry = 12977, Text0_0_loc2 = 'Flétrissure, flétrissure, flétrissure… Il n\'est question que de flétrissure par ici. C\'est si monotone !$B$BVoilà, voilà. Vous avez des familiers, pas vrai ? Oui, bien sûr. Bien sûr. Des petits animaux de compagnie. Oui. J\'ai un petit mélange pour eux. Une petite mixture. Elle va en faire des cad- des créatures fortes et malines ! Oui, oui. Très fortes.$B$BEssayez !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12977;
UPDATE locales_npc_text SET entry = 12978, Text0_0_loc2 = 'Faites attention. Le mélange est instable, oui, et il perd rapidement ses principes actifs. Utilisez-le sans tarder ! Et, de préférence… à portée de vue, oui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12978;
UPDATE locales_npc_text SET entry = 12982, Text0_0_loc2 = 'Il y a quelque temps, j\'ai quitté le front pour aller aider à faire le ménage chez nous, à la marche de l\'Ouest. J\'ai l\'impression que c\'était il y a une éternité. Mais louée soit la Lumière, les Défias ne représentent plus une menace.$B$BNous sommes loin de chez nous, ici, mais si nous ne gagnons pas cette guerre, il n\'y aura peut-être plus de chez nous du tout.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12982;
