UPDATE locales_npc_text SET entry = 12991, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12991;
UPDATE locales_npc_text SET entry = 12992, Text0_0_loc2 = null, Text0_1_loc2 = 'Bien ! Arthas a appris la présence des céréales pestiférées à Stratholme et est sur le point de se lancer dans son épuration. Mais il y a quelque chose qui cloche. Je sens une présence étrangère dans cette ligne temporelle, autre que nous. Cela doit vouloir dire que les Infinis sont tapis quelque part.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12992;
UPDATE locales_npc_text SET entry = 12993, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne sais pas, et cela m\'inquiète. Je ferai tout ce qui est en mon pouvoir pour trouver les Infinis. En attendant, ce que vous devez faire, c\'est rester dans l\'entourage d\'Arthas en rejoignant son armée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12993;
UPDATE locales_npc_text SET entry = 12994, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh, je ne parle pas de signer un ordre d\'engagement militaire, mais de vous battre à ses côtés. Il faut vous assurer qu\'Arthas purge bien Stratholme et abatte Mal\'Ganis. Sans Uther et Jaina, il aura besoin de toute l\'aide disponible. Si vous allez voir Arthas, il vous enverra détruire les troupes de Mal\'Ganis. Suivez le mouvement en attendant que je découvre ce que complotent les Infinis. Je vous recontacterai quand j\'en saurai plus.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12994;
UPDATE locales_npc_text SET entry = 12995, Text0_0_loc2 = null, Text0_1_loc2 = 'Bonne chance, et portez-vous bien. Voilà Uther et Jaina qui arrivent.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12995;
UPDATE locales_npc_text SET entry = 12998, Text0_0_loc2 = '<Drakuru tend une main sanglante à travers les barreaux de la cage et vous lance un regard plein d\'attente.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12998;
UPDATE locales_npc_text SET entry = 12999, Text0_0_loc2 = 'À une époque, j\'vous aurais arraché le cœur juste pour voir la tête que vous auriez tirée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 12999;
UPDATE locales_npc_text SET entry = 13001, Text0_0_loc2 = 'Hé, mec !$B$BChuis content d\'vous revoir, … $N, c\'est ça ? Ouais, ouais, c\'est ça, évidemment.$B$BVous et moi, on va être potes. Drakuru, il le sait.$B$B<Un sourire s\'étale lentement sur le visage peinturluré du troll.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13001;
