UPDATE locales_npc_text SET entry = 13009, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est le seul moyen pour qu\'Harry accepte de me parler ! Il faut que je garde ce masque idiot sur la tête en permanence…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13009;
