UPDATE locales_npc_text SET entry = 13012, Text0_0_loc2 = 'Yo, mec ! On se connaît ?$B$BAh ouaiiis, Dasnob il oublie jamais un visage.$B$BVous… Vous étiez en train de me suivre, yo ? Hé, mec, le problème je le connais pas, mais quoi que ce soit, c\'est pas moi que je l\'ai fait !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13012;
